css:
@-moz-document domain(brick-hill.com) {
nav .info {
    border: 1px solid #E7407E;
}
.card .top:not(.absolute).blue {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
.card .top:not(.absolute).red {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
.card .top:not(.absolute).orange {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
.card .top:not(.absolute).green {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
.card .top:not(.absolute) {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
.status-dot.online {
    background-color: #E7407E;
}
.pages .page.active {
    color: #E7407E;
}
button:not(.plain):not([disabled]).blue, .button:not(.plain):not([disabled]).blue {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
button:not(.plain):not([disabled]).orange, .button:not(.plain):not([disabled]).orange {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
button:not(.plain):not([disabled]).red, .button:not(.plain):not([disabled]).red {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
button:not(.plain):not([disabled]).green, .button:not(.plain):not([disabled]).green {
    color: #E7407E;
    background-color: #2e2f32;
    border-color: #E7407E;
}
.crate-types .active, .crate-types :hover {
    border-left: 4px solid #E7407E;
}
.card .footer .playing {
    color: #E7407E;
}
.favorite-text {
    color: #E7407E;
}
.download-page .holder.legacy button.nh-button {
    border-color: #E7407E;
}
.red-text {
    color: #E7407E;
}
.blue-text {
    color: #E7407E;
}
.green-text {
    color: #E7407E;
}
.alert.warning {
    background-color: #E7407E;
}
.input-group .input-button {
    border-left: 1px solid #b5416b;
    background-color: #E7407E;
}

.forum-quote:hover {
    color: #E7407E;
}

.forum-reply:hover {
    color: #E7407E;
}

.report:hover {
    color: #E7407E;
}
}
