.viewButtonLabel, .addonRating, .addonThumbnailContainer, #browseAddons, vbox[type="header-recommended"], #themeSplitter, #themePreviewArea, .addon-description, .button-box image {display: none !important;}
#viewGroup {padding: 0 3px !important;}
radio {margin: 0 !important;}
radio > image {
width: 24px !important;
height: 24px !important;
margin-bottom: 4px !important;
}
#extensionsBox, #styles-container, #styles {margin: 1px 0 !important;}
richlistitem image {
width: 16px !important;
height: 16px !important;
}
.messageText {margin: 2px 0 2px 4px !important;}
#extensionsView {min-width: 0 !important;}
richlistitem {
min-width: 0 !important;
padding: 6px 4px 2px !important;
}
.addonIcon {
width: 16px !important;
height: 16px !important;
margin: -2px 8px 0 3px !important;
}
.addonName, .selectedStatusMsgs label {
-moz-box-flex: 1 !important;
margin: 1px !important;
}
.addonVersion {margin-top: 2px !important;}
.selectedButtons {margin: 4px 0 !important;}
.uninstallHide {
padding: 0 2px !important;
min-width: 0 !important;
margin: 0 !important;
}
.addon-search-details {margin: 0 !important;}
vbox[type="footer-search"] * {float: left !important;}
.addonTextBox hbox[align="center"] * {-moz-box-flex: 1 !important;}
#styles richlistitem image {margin: -2px 2px 2px 4px !important;}
#styles button {min-width: 0 !important;}
