css:
@-moz-document domain("classic.shoutcast.com") {
  body > table:nth-of-type(1),
  body > table:nth-of-type(2),
  body > table:nth-of-type(3) > tbody > tr:nth-of-type(1),
  body > table:nth-of-type(3) > tbody > tr:nth-of-type(2) > td:nth-of-type(2),
  body > table:nth-of-type(4),
  body > table:nth-of-type(5),
  body > center,
  font > font > b,
  font[color="#ff0000"],
  #chatstuff,
  td[colspan="12"]
    { display: none !important; }

  a[target="_scurl"] {
    width: 700px !important;
    display: block;
    white-space: nowrap;
    float: left;
    overflow: hidden;
    color: white !important;
  }

  br + font[size="1"] {
    color: lightblue !important;
    width: 700px !important;
    display: block;
    white-space: nowrap;
    float: left;
    overflow: hidden;
  }

  .highlighttable {border: gray solid 1px !important; background: #000033 !important; }
  td[bgcolor="#001E32"] { background: #003950 !important; }
  tr:hover > * { background: #000033 !important; }

  img[src="/images/tunein.gif"] {
    width: 0px !important;
    height: 0px !important;
    padding: 24px 24px 0 0;
    background: url(data:image/png;base64,) top right;
  }

}
