@-moz-document domain("utreon.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#channel-navbar .navbar-nav .active>.nav-link,
#channel-navbar .navbar-nav .nav-link.nuxt-link-exact-active,

.input-group-text,
.video-thumbnail .video-preview-overlay,

:focus,
body.dark .btn-primary,
body.dark .input-group-text
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.markdown-body
{
	font-size: 1.5rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.ad-banner-static
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

#app #sidebar-container,
#app.dark #sidebar-container,

.main-content.sidebar-opened
{
	margin: 1.2rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.bg-dark
{
	padding: 1.2rem!important;
}

body.dark .btn-primary
{
	padding: 3px 6px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.container, .container-lg, .container-md, .container-sm, .container-xl
{
	max-width: none!important;
}

.container, .container-lg, .container-md, .container-sm, .container-xl
{
	width: 98%!important;
}
}

