@-moz-document domain("utreon.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

#app .user-card .display-name,
#app .stat .figure,
#app.dark .channel-row .channel-title-container .channel-title,
#channel-navbar .count,

.topics-nav a .topics-label:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

#app,
#app #sidebar-container .section-title,
#app .status,
#app .subscription-tier-card .description,
#app .user-card .stat,

.input-group-text,
.text-dark,
.topics-nav a .topics-label,

[alt="Posts"] .title font,
body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

#app #sidebar-join-poster .title,
#app .subscription-tier-card .title,
#app.dark #sidebar-container .section-title,

.modal-title,
.topic-card .title,
.video-preview .title,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.channel-row .channel-title-container .channel-detail,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.form-title-label,

body.dark .form-title-label,
h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.d-none.d-sm-inline-block,
.info.mt-4.text-center.text-sm-left,
.video-preview .details,

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.text-muted
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

[role="alert"]
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("utreon.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

#app .section,

.custom-control-label:before {
    background: #000000!important;
    background-color: #000000!important;
}

#app .user-card .calendar-icon,
#channel-navbar .followers-icon[data-v-059ddaeb],
#channel-navbar .posts-icon[data-v-059ddaeb],

.filter-area .filter-icon.new,
.rss-icon,
.video-preview .image .play-icon,

[title="Facebook"],
[title="Instagram"],
[title="Subscribe"],
[title="Videos"] .icon
{
    -webkit-mask: none!important;
    mask: none!important;
}

.topic-card.pill
 {
    background: linear-gradient(to right, #3b0202, #0000)!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.navbar,

body.dark .navbar
{
	background: #060505!important;
	background-color: #060505!important;
}


/* Background Transparent 
_____________________________________________________________________________________________________*/

#app,
#app.dark,

.video-thumbnail .video-preview-img,
.bg-white,
.input-group-text,
.video-preview .image .play-icon-container,
.video-preview .video-preview-overlay .play-icon-container,

[style*="background: linear-gradient(rgba"],
body.dark .input-group-text,
body.dark .modal-header,
button
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.btn-primary,

[title="Follow"],
[title="Followed"],
[title="follow"],
[title="followed"],
body.dark .btn-primary
{	
	background: radial-gradient(circle, #031017 0%, #0a587f 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-primary::before,

[title="Follow"]::before,
[title="Followed"]::before,
[title="follow"]::before,
[title="followed"]::before,
body.dark .btn-primary::before
{
	background: radial-gradient(circle, #0f79af 0%, #031017 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-primary:hover::before,

[title="Follow"]:hover::before,
[title="Followed"]:hover::before,
[title="follow"]:hover::before,
[title="followed"]:hover::before,
body.dark .btn-primary:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.btn-secondary
{	
	background: radial-gradient(circle, #380002 0%, #870101 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.btn-secondary::before
{
	background: radial-gradient(circle, #870101 0%, #380002 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.btn-secondary:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

body.dark .btn-secondary
{	
	background: radial-gradient(circle, #651c03 0%, #e1410b 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

body.dark .btn-secondary::before
{
	background: radial-gradient(circle, #e1410b 0%, #651c03 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

body.dark .btn-secondary:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#app #sidebar-container,
#app.dark #sidebar-container,
	
.main-content.sidebar-opened
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

#app #sidebar-join-poster,
#app .subscription-tier-card,
#app .user-card,
#app.dark #sidebar-join-poster,

.bg-dark:not(.modal-header),
.bg-light,
.blog-post,
.channel-card,
.dropdown-menu
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#app #sidebar-container:hover,
#app #sidebar-join-poster:hover,
#app .subscription-tier-card:hover,
#app .user-card:hover,
#app.dark #sidebar-container:hover,
#app.dark #sidebar-join-poster:hover,

.bg-dark:not(.modal-header):hover,
.bg-light:hover,
.blog-post:hover,
.channel-card:hover,
.dropdown-menu:hover,
.main-content.sidebar-opened:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.alert-secondary
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

#channel-navbar,
#utreon-plus-splash
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

#utreon-plus-splash
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.dropdown-item:active,
.dropdown-item:focus,
.dropdown-item:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.title.ml-2.d-none.d-md-inline-block
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

