css:
@-moz-document domain("wikipedia.org") {
#mw-head {
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
    background-color: #383535;
}



.vector-menu-tabs .selected a, .vector-menu-tabs .selected a:visited {
    color: black;
    text-decoration: none;
    background-color: gray
}





.vector-menu-tabs li a {
    background-position: right bottom;
    color: black;
    background-color: gray;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    display: block;
    float: left;
    height: 3.07692308em;
    position: relative;
    padding-top: 1.25em;
    padding-left: 8px;
    padding-right: 8px;
    font-size: 0.8125em;
    cursor: pointer;
}









.vector-search-box-input {
    background-color: lightgray;
    color: black;
    width: 100%;
    height: 2.15384615em;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border: 1px solid #a2a9b1;
    border-radius: 2px;
    padding: 5px 2.15384615em 5px 0.4em;
    box-shadow: inset 0 0 0 1px transparent;
    font-family: inherit;
    font-size: 0.8125em;
    direction: ltr;
    -webkit-transition: border-color 250ms,box-shadow 250ms;
    transition: border-color 250ms,box-shadow 250ms;
    -webkit-appearance: none;
    -moz-appearance: textfield;
}






#mw-panel {
    background-color: #383535; 
}





.vector-menu-portal .vector-menu-content li a {
    color: black
}










.mw-body {
    padding: 1.25em 1.5em 1.5em 1.5em;
    background-color: #383535;
}







.mw-parser-output #mp-topbanner, .mw-parser-output .mp-h2, .mw-parser-output #mp-left {
    color: #000;
    background-color: gray
}



.mw-parser-output #mp-right {
    width: 45%;
    border-color: #cedff2;
    background: #9e9e9e;
    color: black;
}




.mw-parser-output #mp-left .mp-h2 {
    background: lime;
    border-color: green;
    border-radius: 15px 50px;
    border-style: inset;
    border-width: 5px;
}



.mw-parser-output #mp-right .mp-h2 {
    background: #cedff2;
    border-color: #a3b0bf;
    border-radius: 15px 50px;
    border-style: inset;
    border-width: 5px;
}



.mw-parser-output #mp-lower {
    margin-top: 4px;
    border-color: gray;
    background: #9e9e9e;
    border-radius: 15px 50px;
    border-style: inset;
    border-width: 5px;
}


.mw-parser-output #mp-tfp-h2 {
    background: #434343;
    border-color: #201f1f;
    border-radius: 15px 50px;
    border-style: inset;
    border-width: 5px;
}

.mw-parser-output #mp-other-lower .mp-h2 {
    background: gray;
    border-color: lightgray;
    color: black;    
    border-radius: 15px 50px;
    border-style: inset;
    border-width: 5px;
}




.mw-parser-output #mp-tfa, .mw-parser-output #mp-dyk, .mw-parser-output #mp-itn, .mw-parser-output #mp-otd, .mw-parser-output #mp-other-lower>div {
    padding: 0.1em 0.6em;
    color: black
}





.mw-footer {
    padding: 1.25em;
    background-color: #434343;
}



html {
    font-size: 100%;
    background-color: #434343
}








.infobox {
    border: 1px
 solid black;
    border-spacing: 3px;
    background-color: gray;
    color: black;
    margin: 0.5em 0 0.5em 1em;
    padding: 0.2em;
    float: right;
    clear: right;
    font-size: 88%;
    line-height: 1.5em;
    width: 22em;
    border-radius: 15px 50px;
    border-style: inset;
    border-width: 5px;
}



.toc, .toccolours {
    border: 1px
 solid black;
    background-color: gray;
    padding: 5px;
    font-size: 95%;    
    border-radius: 15px 50px;
    border-style: inset;
    border-width: 5px;
    color: black
}






p {
    display: block;
    margin-block-start: 1em;
    margin-block-end: 1em;
    margin-inline-start: 0px;
    margin-inline-end: 0px;
    color: black;
}





div.thumbinner {
    border: 1px
 solid gray;
    padding: 3px;
    background-color: lightgray;
    font-size: 94%;
    text-align: center;
    overflow: hidden;
        border-style: inset;   
border-radius: 15px;
    color: black;
}





.mw-mmv-above-fold {
    width: 100%;
    height: 56px;
    min-height: 56px;
    position: relative;
    padding-bottom: 30px;
    background-color: gray;
    border-width: 0px
}



.mw-mmv-image-metadata {
    width: 100%;
    position: relative;
    margin-top: -30px;
    border-top: 1px 
        solid #c8ccd1;
    background-color: gray;
    padding-top: 2px;
    color: black
}



.mw-mmv-image-desc {
    font-size: 0.95em;
    color: black;
}


.mw-ui-button.mw-ui-progressive:not(:disabled) {
    background-color: #3366cc;
    color: #fff;
    border-color: #3366cc;
    border-radius: 15px 50px;
    border-style: inset;
    border-width: 5px;
}




.wikitable {
    background-color: gray;
    color: black;
    margin: 1em 0;
    border: 1px
 solid #a2a9b1;
    border-collapse: collapse;
}



.mw-references-columns {
    -webkit-column-width: 30em;
    -moz-column-width: 30em;
    column-width: 30em;
    margin-top: 0.3em;
    color: black;
}


.mw-parser-output .reflist-lower-alpha {
    list-style-type: lower-alpha;   
    color: black;
}



.mw-parser-output .reflist-columns {
    margin-top: 0.3em;
    color: black;
}



.catlinks {
    border: 1px
 solid #a2a9b1;
    background-color: gray;
    padding: 5px;
    margin-top: 1em;
    clear: both;
}



.navbox th, .navbox-title {
    background-color: lightgray;
}



body {
    background-color: gray;
    overflow-y: scroll;
}



.mw-parser-output .navbox {
    box-sizing: border-box;
    border: 1px
 solid #a2a9b1;
    width: 100%;
    clear: both;
    font-size: 88%;
    text-align: center;
    padding: 1px;
    margin: 1em
    auto 0;
    color: black;
    background-color: gray;
}



.mw-parser-output .navbox-odd {
    background-color: gray;
}



.mw-parser-output .navbox+.navbox, .mw-parser-output .navbox+.navbox-styles+.navbox {
    margin-top: -1px;
    background-color: gray;
}
}
