css:
@-moz-document domain("crunchyroll.com") {
:root {
 	--var-element-border-rad: 0.3rem;   
}

.cf:before, .cf:after {
 	visibility: hidden;   
}


img {
 	max-width: 100%;   
}

.cr-expo-banner {
 	min-width: 0px;   
}

#template_scroller {
	overflow-x: hidden;   
    padding: 0 1rem;
    margin: 0;
}

#template_container {
    width: 100%;
	max-width: 1920px;		   
    padding: 0;
    
}

#showmedia, #source_browse #container.cf, .main-page #template_body, #source_showview #container.cf{
 	display: flex;
    flex-direction: row;
    justify-content: space-between;
    gap: 1rem;
}



#showmedia:before, #source_browse #container.cf:before, .main-page #template_body:before, #source_showview #container.cf:before {
 	display: none;   
}

.game-banner-wrapper, #showview-content-header {
 	display: none;   
}

#source_showview {
 	margin: 0;
    width: 100%;
    
}

#source_showview .season-dropdown, #source_showview .season-dropdown.open {
 	background-position-x: calc(100% - 1rem);
}



.showmedia-header {
 	margin-top: 2rem;   
}

.welcome-crnews-item {
    display: flex;
    flex-direction: row;
}

.welcome-crnews-item a {
 	flex-shrink: 0;   
}

.welcome-crnews-info {
 	max-width: 400px;
    min-width: 200px;
    width: auto;
}

.welcome-latest-videos {
    width: 100%;
}

#welcome_left .welcome-block .welcome-more-bottom {
 	display: block;   
}


#showmedia::after, #showmedia:before {
 	display: none;   
}

#main_content {
 	width: 100%;   
}

#main_content.left, #welcome_left {
    flex: 1;
}

#sidebar {
    display: none;
 	max-width: 300px;   
    float: none;
}

#showmedia_video_box {
 	width: 100%;
    height: auto;
    aspect-ratio: 16/9;
}

.game-banner-wrapper .game-banner-container {
	width: 100% !important;   
    display: flex;
    justify-content: center;
}

.collection-carousel {
 	width: 100%;   
}

.collection-carousel .collection-carousel-contents {
 	width: 100%;   
}

#sidebar {
 	min-width: 330px;   
}

.g-recaptcha div {
	width: 100% !important;
}

#showmedia_free_trial_signup {
    border-radius: var(--var-element-border-rad);
}

.white-wrapper {
 	border-radius: var(--var-element-border-rad);
}

.group-item .wrapper {
 	padding: 0.5rem;   
    border-radius: var(--var-element-border-rad);
}


@media screen and (min-width: 1024px) {
    #template_container.template-container {
    	width: 90vw;
    	max-width: 1920px;		   
    }
    #sidebar {
    	display: block;
    }
    
}

@media screen and (min-width: 1250px) {
        #welcome_left .welcome-block > div {
 		display: grid;
    	grid-template-columns: 1fr 1fr;
		gap: 1rem;
                                                                                    
	}
}

.guestbook-commentform .widget-form-td {
 	padding: 1rem;   
}

.guestbook-mug img{
 	border-radius: var(--var-element-border-rad);
    min-height: 50px;
    object-fit: cover;
}

.guestbook-list {
 	padding: 0;   
}

.guestbook-list .guestbook-name {
 	margin-top: 0.5rem;   
    font-size: 1rem;
}

.guestbook-list .guestbook-time {
 	font-size: 0.825rem;   
}

.guestbook-list .guestbook-body {
 	margin-top: 1rem;
    margin-left: 0.5rem;
    font-size: 1rem;
}

#allCommentsList.ulist  li {
 	padding: 1rem 1rem;   
    
}

.replylist {
 	border-left: 3px solid #d7d7d7 !important;   
    padding-left: 1rem;
}

.more-replies {    
	width: auto;
    max-width: 10rem;
    margin-left: 1rem;
    padding: 0.5rem 1rem;
    border: 1px solid #d7d7d7 !important;
    border-radius: 99rem;
    text-align: center;
    margin-bottom: 1rem;
}

.more-replies a {
    width: 100%;
 	text-align: center;   
}

.reply-list form {
	padding: 1rem;   
}

.comments #allCommentsList .reply-form, #main_content textarea {    
 	width: 100%;   
    max-width: 100%;    
    min-height: 50px;    
    border-radius: var(--var-element-border-rad);
}

.comments .jqEasyCounterMsg {
	position: unset;
    width: 100% !important;
}

.guestbook-list {
 	border: none;
}

.guestbook-links {
 	margin: 0;   
}

#footer {
	background-color: #f3f3f3;   
}
}
