css:
@-moz-document domain("app.contentful.com") {

/*
 * Form elements
 */
.workbench-layer--is-current .cf-workbench-content-type__text {
	max-width: 1000000px;
}
.entry-editor main button,
.entry-editor main iframe,
.entry-editor main input,
.entry-editor main textarea {
	background:#f7f9fa !important;
    border: 1px solid rgb(207, 217, 224) !important;
    border-radius: 6px !important;
}
.entry-editor main iframe {
    padding: 8px 0 6px 0;
    box-sizing: content-box;
}
.entry-editor main th:nth-child(2),
.entry-editor main td:nth-child(2) {
    width:40%;
}

/*
 * Header bars
 */
/* lighter/darker bottom borders so they match together */
main[class*="Workbench__Workbench"] .app-top-bar {
	    height: 56px;
    min-height: 56px;
}
main[class*="Workbench__Workbench"] > div > header {
	height: 54px;
    overflow:hidden;
	border-bottom: 1px solid hsla(205deg, 22%, 85%, 0.5);
}
main[class*="Workbench__Workbench"] div[class*="Tabs__Tabs__divider--horizontal"] {
    box-shadow: inset 0 -1px 0 0 #cfd9e0;
}
/* make both top bars light grey the same */
main[class*="Workbench__Workbench"] > div:first-child > div:first-child {
	padding-left: 0;
    background-color: #f7f9fa;
}
main[class*="Workbench__Workbench"] > div:first-child > div:first-child > div:before {
	height: 1px;
    background: hsla(211deg, 97%, 45%, 0.25);
}

/*
 * Each field
 */
.entity-editor__field-heading label {
    color: #111;
    font-weight: 700;
    font-size: 14px;
    margin: 0 0 4.5px 0.5px;
}
.entity-editor__field-group {
	margin: 0;
    border: none;
    padding: 8px 22px 24px;
}
.entity-editor__field-group + .entity-editor__field-group {
	border-top: none;
}

/*
 * Add content from other table - picker field
 */
.entity-editor__control-group > div > div {
    padding: 0;
    background: none;
    border: none;
    justify-content: start;
}

/*
 * Field errors and messages
 */
main[class*="Workbench__Workbench"] input,
main[class*="Workbench__Workbench"] textarea {
	margin:0;
}
main[class*="Workbench__Workbench"] input + div, 
main[class*="Workbench__Workbench"] textarea + div {
    transform-origin: bottom left;
    transform: scale(0.75);
    opacity:0.75;
}
main[class*="Workbench__Workbench"] textarea + div {
    margin: -3px 0 0 !important;
}
main[class*="Workbench__Workbench"] input + div {
    margin: 3px 0 0 !important;
}
main[class*="Workbench__Workbench"] ul[data-test-id="validation-errors"] {
    margin: -1px 0 0 0 !important;
    transform-origin: bottom left;
    transform: scale(0.5);
    opacity:0.5;
}
main[class*="Workbench__Workbench"] ul[data-test-id="validation-errors"] li {
    margin: 0 !important;
    font-size: 150%;
}
.entity-editor__field-group[aria-invalid=true] {
    border-left-color: rgba(218, 41, 74, 0.5);
}

}
