css:
@-moz-document url-prefix("https://cyberlearner.com/CyberLearner/DailyEntries.php") {
* {
	font-family: 'Quicksand', sans-serif;
	font-size: 16px;
    color: #FFFFFF;
}
.reportheader {
    background-color: #121212;
	color: #ffffff;
}

.selfassessment {
    background-color: #121212;
	color: #ffffff;
}

body {
    background-color:  #121212;
}

.dailyEntryContent, .dailyEntryHeader {
    background-color: #121212;
    vertical-align: middle;
	color: #FFFFFF;
	text-align: center;
}

table {
    border-collapse: collapse;
    border-color: transparent;
}

TEXTAREA {
    background-color: #212121;
    border: 0px;
    width: 100%;
	border-radius: 10px;
	border-color: none;
	border: none;
	padding-top: 4px;
	padding-bottom: 4px;
	padding-left: 6px;
	padding-right: 6px;
	font-family: 'Roboto', sans-serif;
	font-size: 16px;
    color: #FFFFFF;
}

TEXTAREA:active {
	border: none;
}

INPUT {
	background-color: #212121;
	border: none;
	border-radius: 10px;
}

INPUT:active {
	background-color: #1a1a1a;
}

TD {
    text-align: middle;
    background-color: #121212;
    padding: 10px;
    color: #FFFFFF;
}

a:link {
	font-weight: 600;
	text-decoration: none;
	color: #c38fff;
}

a:visited {
    color: #03dac6;
}
  
a:hover {
	text-decoration: underline; 
}

TD.sessionApproved {
	background-color: #4caf50;
	color: #121212;
	font-weight: bold;
	border-radius: 10px;
}
}
