css:
@-moz-document domain("boxitsoft.atlassian.net")

{
    html {
        filter: invert(92%) hue-rotate(180deg) brightness(150%) contrast(100%);
        background: white;
    }

    body {
        background: white;
    }

    .css-ob4lje, span[role="img"] {
        filter: invert(100%) hue-rotate(180deg) brightness(130%) contrast(100%);
    }

    img, svg, mp4, video, .emoji-common-emoji-sprite {
        filter: invert(100%) hue-rotate(180deg) brightness(105%) contrast(100%);
    }

    /* Fix bug with white edges on sprint board */
    #gh {
        padding: 0 40px;
    }

    #content {
        margin: 0;
    }
    .ghx-issue {
	background: #eee;
    }
    .gPlhNM {
        background-color:rgba(255,255,255,0.8);
    }
  /*some buttons and badges*/
	.css-vvvfhy:not(:active), .css-vvvfhy:disabled, .bar-progress-badge, #atlassian-navigation-notification-count {
        filter: invert(100%) hue-rotate(180deg);
    }
    /* Fix scroll background problems */
    [data-testid="Content"]>div:first-child {
        background: white;
    }

    /* LOGO */
    .css-s41dnj>img {
        content: url("https://machinga.net/machinga-brain-system/frontend/web/brain/assets/menu/icon-wikinga.fw.png");
    }

    .css-s41dnj::after {
        content: "MACHINGA GAMES";
        font-size: 12px;
        font-weight: 1000;
    }
}
/*Based on JIRA Dark 2020*/
