css:
@-moz-document domain("xiaomiadictos.com")
{
  :root
  

body {
    font-family: var(--body-font);
    line-height: 1.5;
    color: #4d5053;
    background: #000;
    -webkit-text-size-adjust: 100%;
    -webkit-tap-highlight-color: transparent;
    overflow-x: hidden;
}

.post-title {
    color: #5a6a7a;
    font-family: var(--title-font);
    line-height: 1.36;
}

.pagination-numbers .page-numbers, .pagination-numbers .post-page-numbers, .pagination-numbers a {
    display: block;
    color: #505050;
    padding: 0 10px;
    margin-right: 6px;
    line-height: 30px;
    font-weight: 500;
    border-radius: 2px;
    border: 1px solid #1c1c1c;
}
 
.grid-post .post-meta .meta-above {
    position: relative;
    padding: 0 14px;
    margin-top: -16px;
    background: #3a3b3c57;
    line-height: 26px;
}


.s-light {
    --body-color: #4d5053;
    --c-post-meta: #a6a6a6;
    --c-separator: #1c1c1c;
    --c-separator2: #1c1c1c;
}


.breadcrumbs-b:not(.is-full-width)>.inner {
    background: #3a3b3c57;
    padding: 16px 25px;
}


:root {
    --c-main: #e54e53;
    --main-width: 1078px;
    --c-excerpts: var(--c-contrast-500);
    --excerpt-size: 13px;
    --sidebar-pad: 25px;
    --body-font: "Open Sans", system-ui, sans-serif;
    --title-font: "Roboto Slab", Georgia, serif;
    --title-size-xs: 13px;
    --title-size-s: 14px;
    --title-size-n: 14px;
    --title-size-m: 15px;
    --title-size-l: 18px;
    --title-size-xl: 23px;
    --title-fw-semi: 400;
    --title-fw-bold: 500;
    --list-p-title: var(--title-size-m);
    --dark-color: #19232d;
    --c-links: #5a6a7a;
    --c-headings: #19232d;
}


.post-content h1, .post-content h2, .post-content h3, .post-content h4, .post-content h5, .post-content h6, .textwidget h1, .textwidget h2, .textwidget h3, .textwidget h4, .textwidget h5, .textwidget h6 {
    margin-top: 1.35em;
    margin-bottom: 0.75em;
    font-weight: var(--title-fw-bold);
    color: #384858;
    font-family: var(--text-h-font,inherit);
    line-height: 1.5;
}


.site-s-light .entry-content {
    color: #6c6b6b;
}

.dark .nav-tab>a {
    color: #525f6c!important;
    text-shadow: none;
}

.dark .tab-conversation.active>a {
    color: #525f6c!important;
}

.textarea-outer-wrapper .textarea-wrapper {
    -webkit-appearance: none;
    background: #26262885;
    border: 2px solid #dbdfe400;
    position: relative;
    border-radius: 4px;
    margin: 0;
}

body.dark {
    color: #525f6c;
}

.dark .input--text {
    background: #26262885;
    box-shadow: none;
    border-color: #e7e9ee00;
}

.post-actions {
    border-radius: 0 0 2px 2px;
    border-top: solid 2px #1c1c1c;
}

.the-post-tags a {
    display: inline-block;
    margin-right: 5px;
    margin-bottom: 3px;
    padding: 0 10px;
    background: #bd422e;
    color: #000;
    font-size: 13px;
    letter-spacing: .03em;
    line-height: 28px;
    border-radius: 2px;
}

.smart-head-bot.s-dark, .s-dark .smart-head-bot {
    --c-bg: #19232dba;
    backdrop-filter: blur(12px) !important;
    border: 0;
}

.dropdown-menu {
    font-size: 13px;
    border: 2px solid #687a86;
    border-radius: 3px;
    background: #000000c9 !important; 
    box-shadow: none;
    min-width: 130px;
    z-index: 1001;
    backdrop-filter: blur(12px) !important;
    
}

.textarea-outer-wrapper .textarea-wrapper {
    -webkit-appearance: none;
    background: #161616;
    border: 2px solid #ffffff2e;
    position: relative;
    border-radius: 4px;
    margin: 0;
}

.dark .input--text {
    background: #161616;
    box-shadow: none;
    border-color: #ffffff2e;
}

.textarea {
    color: #ebeaea;
}

.rc-anchor-light {
    background: #1a1a1a;
    color: #9f9f9f;
}

.rc-anchor-light.rc-anchor-normal {
    border: 1px solid #1a1a1a;
}

.dark .proceed__button {
    background: rgb(30 30 30);
    color: #7a7a7a;
}

.btn {
    margin-bottom: 0;
    font-size: 14px;
    vertical-align: middle;
    cursor: pointer;
    box-shadow: inset 0 1px 0 rgb(241 241 241 / 0), 0 1px 2px rgb(0 0 0 / 5);
}

.post-actions {
    border-radius: 0 0 2px 2px;
    border-top: solid 2px #ffffff2e;
}

.wysiwyg .vertical-separator {
    border-right: 2px solid #dbdfe400;
    margin: 0 5px;
}

.unread .notification-icon {
    color: #f1354b;
}
 }
