css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#nav-bar { -moz-appearance: none !important;
 font-size: 100% !important; 
 max-height: 30px !important; min-height: 25px !important;
 margin: -2px 0  -5px 0 !important;  
 padding-top: 0px !important;  padding-bottom:2px !important;
 padding-right: 5px !important;
 padding-bottom: 5px !important;
 border: none !important;
 padding-left: 0px !important;  padding-right: 0px !important;  } 

/*REMOVE URL ID BUTTON, STAR BUTTON, MAGNIFYING GLASS*/
#go-button  {display:none !important}
#identity-box  {display:none !important}
.search-go-button  {display:none !important}
#star-button {display:none !important}
#feed-button {display:none !important}
.searchbar-engine-button {display:none !important}

/* ----Center tabs text---- */
.tab-text {text-align: center !important; margin-top: -2px !important;}

/* ---REMOVE TAB ICON---*/
.tab-icon-image {display:none !important}

/* ----Changes the height of the tabbar---- */
.tabbrowser-strip {height: 20px !important; border: none !important;}
.tabbrowser-tab, .tabs-alltabs-button, .tabs-closebutton {margin-top: 0px !important;}
.tabbrowser-tab > .tab-text {margin-left: 2px!important; margin-right: 0px!important;}

.tab-close-button {display:none!important}

.tabs-newtab-button {display:none!important}

.tabs-alltabs-button { display: none !important; }

.tabs-container > stack { display: none !important; }

.tabbrowser-tab:hover { 
 background-color: ThreeDHighlight !important;
 padding: 1px !important;}
