@-moz-document domain("lolz.guru") {
@keyframes snow {
      0% {background-position: 0px 0px, 0px 0px, 0px 0px;}  
     100% {background-position: 500px 1000px, 400px 400px, 300px 300px;}  
  } 

  body {
