css:
@-moz-document domain("bild.de") {
html {
    background: #000 !important;
}

body {
    background: #fff !important;
    filter: invert(93%) hue-rotate(180deg) !important
}

:fullscreen,
iframe,
img,
svg,
video,
.slider,
.page-header__logo {
    filter: invert(1) hue-rotate(180deg) !important
}
}
