css:
@-moz-document domain("tarnkappe.info"), regexp(".*://t.me/tarnkappe_info/.*") {
html {
    background: #000 !important
}

body {
    background: #fff !important;
    filter: invert(93%) hue-rotate(180deg) !important
}

:fullscreen,
iframe,
img,
svg,
video {
    filter: invert(1) hue-rotate(180deg) !important
}
}

@-moz-document regexp(".*://tarnkappe.info/.*") {
div#header-top,
header#header,
footer#footer {
    filter: invert(1) hue-rotate(180deg) !important
}
}

@-moz-document domain("forum.tarnkappe.info") {
header#header,
div.title,
footer_tk#footer_tk {
    filter: invert(1) hue-rotate(180deg) !important
}
}

@-moz-document domain("shop.tarnkappe.info") {
header#header,
div.sprd-info-banner,
div.sprd-promo-header,
div.sprd-header a,
div.sprd-navigation,
footer_tk#footer_tk {
    filter: invert(1) hue-rotate(180deg) !important
}
}

@-moz-document domain("meet.tarnkappe.info") {
div.header,
div#enter_room {
    filter: invert(1) hue-rotate(180deg) !important
}
}

@-moz-document regexp(".*://tarnkappe.info/banner/") {
picture {
    filter: invert(1) hue-rotate(180deg) !important
}
}
