css:
@-moz-document domain("www.coingecko.com")
{
  html
{
filter: invert(90%) brightness(100%) contrast(100%);
background: white;
}

img, svg
{
filter: invert(100%) hue-rotate(360deg) brightness(105%) contrast(100%);
}

div, span, a, p, h1, h2, h3, h4, h5, h6 {
filter: hue-rotate(180deg);
}

.main-logo {
filter: hue-rotate(180deg);
}

td > a > img {
filter: invert(100%) hue-rotate(180deg);
}
}
