css:
@namespace url(http://www.w3.org/1999/xhtml);

a[href*="amazon"][href*="-22"], 
a[href*="afl.rakuten"], 
a[href*="liveads.jp"], 
a[href*="doubleclick.net"], a[href*="google.com"][href*="adsense"],
a[href*="ad.agilemedia.jp"], 
a[href*="rssad.jp"], 
a[href*="dlsite.com"], 
a[href*="ainte.jp"] {
  color: #b8860b !important;
  text-decoration: none;
  background-color: #f0e68c !important;
  border-top: 1px dotted #b8860b !important;
  border-bottom: 1px dotted #b8860b !important;
}

a[href*="amazon"][href*="-22"]:hover, 
a[href*="afl.rakuten"]:hover, 
a[href*="liveads.jp"]:hover, 
a[href*="doubleclick.net"]:hover, a[href*="google.com"]:hover[href*="adsense"]:hover,
a[href*="ad.agilemedia.jp"]:hover, 
a[href*="rssad.jp"]:hover, 
a[href*="dlsite.com"]:hover, 
a[href*="ainte.jp"]:hover {
  text-decoration: underline;
}
