css:
@-moz-document url-prefix("https://reg.uci.edu/RespDashboard") {
tr {
    height: 0rem !important;
}
td {
    padding-bottom: 0 !important;
    padding-top:0 !important;
    border-bottom: 1px solid rgba(0, 0, 0) !important;
    color: white !important;
}
th {
    padding-bottom: 0 !important;
    padding-top:0 !important;
}
.hedtech-spacing-responsive.hedtech-container-spacing.hedtech-spacing-standard {
    padding:0.5rem;
    background-color: #333;
}
h2 {
    padding-bottom: 0 !important;
}
.hedtech-spacing-standard div div div div div div {
    padding:0;
    background-color:#333;
    color:white;
}
.hedtech-spacing-standard div div {
    padding:0;
    padding-right: 1.5rem;
}
.hedtech-spacing-standard div div div div {
    color:white;
    background-color:#333;
    padding-right: 1.5rem;
}
.hedtech-spacing-standard div {
    color:white;
    background-color:#333;
}
.dashboard-body {
    background-color:#0f0f0f;
}
h2 span:last-child {
    background-color:#333 !important;
}
p {
    color:white !important;
}
footer {
    background-color:#333 !important;
}
.menu-bar {
    background-color:#333 !important;
}
.themed {
    background-color:#333 !important;
}
.utility div {
    background-color:#333 !important;
}
a {
    color:white !important;
}
.useFluidTransition {
    background-color:#333;
}
h1 {
    color:white;
}
main div:nth-child(2) div div div div {
    background-color:#333 !important;
    color:white !important;
}
input {
    color:white !important;
}
}
