css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.phpclasses.org") {


html, body, talbe, .framed, .back, .menu, .box, .nbtu, .nbtd {
background-color:#fff !important;
border:1px solid #eee !important;
font-size:9pt;
}
.classesdescription, .menudescription{
font-size:8pt;
}
.nbgd, .nbpu{
border:0px solid #fff !important;
border-top:1px solid #eee !important;
}
.nbgu, .nbpd{
border:0px solid #fff !important;
border-bottom:1px solid #eee !important;
}
.tabcontent, .nblu a, .nbtu a{
vertical-align:middle !important;
padding-top:1px !important;
}
.tabcontent, .nblu  {
background-color:#ffe055!important;
border:1px solid #eee!important;
font-size:9pt;
}
h2{
font-size:10pt !important;
}
h1{
font-size:32pt !important;
background-color: #eee !important;
}
h2 a{
color:#00a;
font-size:11pt;
text-transform: uppercase;
text-align:center;
}
h2 a:visited{
color:#00a;
}
.even, .light{
background-color:#fff !important;
font-size:9pt;
}
.odd, .dark{
background-color:#f0f0fa !important;
font-size:9pt;
}
.highlight{
background-color:#ffefaa !important;
font-size:9pt;
}
.noscript-show span{
display:none !important;
}
body #EchoTopic center table tbody tr td{
width:150px !important;
}
body #EchoTopic table tbody tr td{
width:0px !important;
}
 h2.menulink a{
font-size:10pt !important;
}
td.menuicon a img{
float:right;
width:40px !important;
height:30px !important;
}
}
