css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("outeverywhere.com") {

.fixed { width:100%; position:fixed !important; z-index:100; margin-top:-22px !important; background-color:#ddd !important; -moz-opacity:0.9 !important; border-bottom:1px solid #bbb !important; }

#outeverywhere { margin-top:22px !important; }

.magical { margin-top:22px !important; height:22px !important; overflow:hidden !important; border-right:1px solid #bbb !important; width:551px !important; }

FORM[target=google_window] { display:none !important; }

.magical FORM { margin:0px; overflow:hidden !important; font-size:100% !important; height:22px; margin-top:-1px !important; overflow:hidden !important;

}

.magical { background-color:#eee !important; border-top:1px solid #bbb !important; }

.minibar { padding-top:1px !important; font-size:90% !important; overflow:hidden !important; background-color:transparent !important; height:22px !important; overflow:hidden !important; }

.minibar IMG { float:left !important; margin-right:3px !important; margin-top:-1px !important;

}

.minibar INPUT { margin-top: 0px !important; border:1px solid #aaa !important; }

.minibar DIV { border: 1px solid #eee !important; }

FORM > .minibar:first-child { margin-right:-25px !important; }

FORM > .minibar:first-child:after { white-space:pre; content:"\2000\2000\2000qf:";

}

.magical #quickfind { margin-top:3px !important; width:70px !important; overflow:visible !important; }

.minibar LABEL { display:none; }

.minibar INPUT[name="go"] { display:none !important; }

A[title="Please logout of your account before closing the site"] { display:block; }



#menu { left:10px !important; margin-left:0px !important; }

#content { left:0px !important; padding:0px !important; width:auto !important; left:0 !important; margin-left:210px !important; margin-right:15px !important; }

#content H2 { border:1px solid #aaa; }

#content H1 { padding-top:7px !important; margin-bottom:-7px !important; font-size:1.4em !important; letter-spacing:normal !important; }

#content H1 A { letter-spacing:normal !important; }

.postings, .thattable, .events, .inbox TABLE { border:1px solid #aaa !important; border-collapse:separate !important; border-spacing:0px !important; }

.thattable TD { border-bottom:1px solid #fff; }

.events TH, .inbox TABLE TH { background-color:#ddd !important; }

.forumpost { background-color:transparent !important; border-style:none !important; }

.posttop { border:1px solid #aaa !important; border-bottom:1px dotted #aaa !important; background-color:#ddd !important; -moz-border-radius-topleft:8px; }

.postmid { background-color: #eee !important; border-left:1px solid #aaa !important; border-right:1px solid #aaa !important; }

.postbot { background-color: #eee !important; border-left:1px solid #aaa !important; border-right:1px solid #aaa !important; border-bottom:1px solid #aaa !important; -moz-border-radius-bottomleft:8px; -moz-border-radius-bottomright:8px; margin-top: 0px !important; padding-top: 3px !important; padding-bottom: 3px !important; }

.thumbs { background-color: #eee !important; border-left:1px solid #aaa !important; border-right:1px solid #aaa !important; padding:1px !important; width:auto !important; overflow:auto !important; margin:0px !important; padding-left:12px !important; }

.placeholder { border:1px solid #aaa; }

.infobox { border:1px solid #aaa !important; -moz-border-radius:8px; margin-left:0 !important; margin-bottom:15px !important; }

.infobox SPAN[class] { display:block !important; margin-bottom:10px !important; }

.allheaders { width:180px !important; height:45px !important; border-left:1px solid #aaa; float:right; position:relative !important; }

.allheaders SPAN { position:absolute; bottom:0px; font-size:.8em !important; background-color:#333 !important; left:0px !important; padding-left:3px !important; color:#fff !important; width:177px; text-align:center; -moz-opacity:0.7; }

.links A { border:1px solid #aaa !important; }

.links A:hover { background-color:#ccc !important; color:#000 !important; }

#nav A { font-family: ariel, helvetica, sans-serif !important; background-color:#555; text-transform:capitalize !important; font-size:105% !important; display:block !important; padding:4px !important; padding-top:7px !important; padding-left:5px !important; border:0px !important; width:82px !important; border-bottom:1px solid #000 !important; border-right:1px solid #000 !important; border-left:1px solid #000 !important; }

#nav + DIV { display:none !important; }

#side { margin-bottom:5px !important; overflow:auto; }

ul#side LI A { border:1px solid #aaa !important; text-transform:capitalize !important; }

ul#side li a:hover { background-color:#ccc !important; color:#000 !important; }

ul#side li { width: 178px !important; margin:0px !important; margin-bottom:2px !important;

}Instr .featured { border:1px solid #aaa !important; width:170px !important; -moz-border-radius:8px; margin:0px 0px 10px 0px !important; padding:8px !important; }

.featured P { padding:0px !important; padding-bottom:8px !important; margin:0px !important; margin-bottom:8px !important; border-bottom:1px dotted #aaa !important; }

.featured H2, .featured H3 { margin:0px !important; padding:0px !important; text-transform:none !important; margin-bottom:5px !important; letter-spacing:normal !important; }

.featured P.last { border-bottom:0px dotted #aaa !important; }

.featured UL { border-bottom:1px dotted #aaa !important; }

.featured UL.last { border-bottom:0px dotted #aaa !important; }

#footer { -moz-border-radius:8px; }

.textdiv { padding: 1.2em 1em 0em 1em !important; text-indent:0em !important; }

.textdiv P { margin-bottom:3px !important; }

h1, h1 a { padding:0px !important; }

}
