css:
@-moz-document domain("hemingwayapp.com")
{
/*Removes Left Sidebar */ 
  .hemingway-desktop-sidebar-container
  {
    display: none;
  }
/* Widens writing area to compensate for more space provided by the hidden sidebar */
  .hemingway-editor-components-container
  {
    flex-basis: 60em;
  }
    
/* Removes padding that was needed for the sidebar */
  .hemingway-editor-container
  {
    display: flex;
    justify-content: center;
    padding: 0px 270px 0px 10px;
  }
/* Removes Logo */
  .hemingway-sidebar .hemingway-logo
  {
    display: none;
  }
/* Changes suggested text color grey so it's readable agaisn't the colored highlights */
  .complexword, .adverb, .qualifier, .passivevoice, .hardreadability, .veryhardreadability, .hemingway-tooltip-suggestion, .hemingway-tooltip
  {
    color: #413232;
  }

  #footer
  {
    display: none;
  }

  #nav-bar
  {
    display: none;
  }
  body
  {
    background-color: #202124;
  }
/* Author Cryptophunk https://steemit.com/@cryptophunk */
/* Makes word "Readability" white */
  .hemingway-stats-container h3
  {
    color: #d3d8e6;
  }
/* Changes readability stats text to white */
  .hemingway-editor-draft-container, .hemingway-stat-items-container
  {
    color: #d3d8e6;
  }  
}
