css:
@-moz-document domain("roblox.com") {
.lower-logo-container {
    position: relative;
    z-index: 2;
    background-color: #fff;
    min-height: 900px;
    padding-top: 120px;
    height: auto;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}



#landing-page-container {
    min-height: 100vh;
    background: #ffffff
}

.dark-theme .theme-bg {
    background: #b4b4b4;
    padding: 20px 30px 20px 30px;
    color: white;
}

.text-logo {
    background-image: url(https://www.freepnglogos.com/uploads/roblox-logo/file-roblox-logo-4.png);
    height: 50px;
    background-repeat: no-repeat;
    background-position: bottom;
    background-size: contain;
    margin: 0 24px;
}




.container-fluid {
    margin-right: auto;
    position: relative;
    display: table;
    width: 1500px;
    height: 40px;
    margin-left: auto;
    padding-left: 0;
    padding-right: 0;

    color: white
}


.light-theme .rbx-header .text-header,
.light-theme .rbx-header .text-header:active,
.light-theme .rbx-header .text-header:focus,
.light-theme .rbx-header .text-header:hover,
.light-theme .rbx-header .text-header:link,
.light-theme .rbx-header .text-header:visited {
    color: white;
    font-weight: bold
}



.light-theme .icon-default-logo-r,
.light-theme .icon-logo-r,
.light-theme .icon-logo-r-95 {
    background-image: url(https://www.freepnglogos.com/uploads/roblox-logo/file-roblox-logo-4.png);
}


.light-theme .icon-default-logo-r,
.light-theme .icon-logo-r,
.light-theme .icon-logo-r-95 {
    background-image: url(https://www.freepnglogos.com/uploads/roblox-logo/file-roblox-logo-4.png);
}


.light-theme .icon-default-logo,
.light-theme .icon-logo {
    background-image: url(https://www.freepnglogos.com/uploads/roblox-logo/file-roblox-logo-4.png);
}


.dark-theme .icon-default-logo,
.dark-theme .icon-logo,
.icon-default-logo,
.icon-logo {
    background-image: url(https://www.freepnglogos.com/uploads/roblox-logo/file-roblox-logo-4.png);
}




.light-theme .icon-default-navigation,
.light-theme .icon-nav-blog,
.light-theme .icon-nav-charactercustomizer,
.light-theme .icon-nav-friends,
.light-theme .icon-nav-giftcards,
.light-theme .icon-nav-group,
.light-theme .icon-nav-home,
.light-theme .icon-nav-inventory,
.light-theme .icon-nav-menu,
.light-theme .icon-nav-message,
.light-theme .icon-nav-my-feed,
.light-theme .icon-nav-notification-stream,
.light-theme .icon-nav-profile,
.light-theme .icon-nav-robux,
.light-theme .icon-nav-search-white,
.light-theme .icon-nav-settings,
.light-theme .icon-nav-shop,
.light-theme .icon-nav-trade {
    background-image: url(https://images.rbxcdn.com/5be09c7c686dbba1984fc1a2bacb772c-navigation_light.svg);
}


.light-theme .rbx-header {
    background: url(http://www.roblox.com/images/RevisedHeader/bg-rbx_header.png) repeat-x;
    border: solid #c7cbce;
    border-width: 0 0 1px;
}


.rbx-header .rbx-navbar-icon-group .age-bracket-label .age-bracket-label-username {
    display: inline-block;
    margin-right: 6px;
    color: white;
}


.rbx-header .rbx-navbar-icon-group {
    margin: 0;
    line-height: 28px;
    float: right;
    color: white
}

.rbx-header .rbx-navbar-icon-group li span[class^=icon] {
    vertical-align: middle;
    color: white;
    margin-top: -2px;
}


.light-theme .icon-default-navigation,
.light-theme .icon-nav-blog,
.light-theme .icon-nav-charactercustomizer,
.light-theme .icon-nav-friends,
.light-theme .icon-nav-giftcards,
.light-theme .icon-nav-group,
.light-theme .icon-nav-home,
.light-theme .icon-nav-inventory,
.light-theme .icon-nav-menu,
.light-theme .icon-nav-message,
.light-theme .icon-nav-my-feed,
.light-theme .icon-nav-notification-stream,
.light-theme .icon-nav-profile,
.light-theme .icon-nav-robux,
.light-theme .icon-nav-search-white,
.light-theme .icon-nav-settings,
.light-theme .icon-nav-shop,
.light-theme .icon-nav-trade {
    background-image: url(https://images.rbxcdn.com/5be09c7c686dbba1984fc1a2bacb772c-navigation_light.svg);
}




.light-theme .text-robux-tile {
    color: #267F00;
    font-weight: bolder;
}





.light-theme .icon-clanpoint,
.light-theme .icon-default-branded,
.light-theme .icon-favorite,
.light-theme .icon-follow-game,
.light-theme .icon-follow-game-gray,
.light-theme .icon-leaderboard,
.light-theme .icon-playerpoint,
.light-theme .icon-robux,
.light-theme .icon-robux-28x28,
.light-theme .icon-robux-burst-banner,
.light-theme .icon-robux-gold,
.light-theme .icon-robux-gray,
.light-theme .icon-robux-white {
    background-image: url(https://www.linkpicture.com/q/robuxtest.png);
    size: 10px
}




.light-theme .btn-growth-sm {
    background: url(http://www.roblox.com/images/RevisedHeader/bg-rbx_header.png) repeat-x;
    color: #fff;
    border-radius: 0px;
    border-image: url(http://www.roblox.com/images/RevisedHeader/bg-rbx_header.png);
    position: static;
}


.btn-control,
.btn-control:active,
.btn-control:link,
.btn-control:visited,
.btn-control:hover {
    border: 1px solid #777;
    padding: 0 6px;
    color: #000;
    text-decoration: none;
    background-color: #ccc;
    text-align: center;
    font-weight: normal;
    cursor: pointer;
    background-position: top;
    display: inline-block;
    *display: inline;
}





.signup-or-log-in .signup-container .signup-input-area .signup-submit-button {
    margin-top: 12px;
    border-color: #007001;
    background-color: #007001;
    background-image: url(https://web.archive.org/web/20140613193728im_/http://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    color: white;
    border-radius: 0px
}



#action-bar-container #action-bar a,
#action-bar-container #action-bar button {
    min-width: 140px;
    margin-right: 20px;
    background: #9e9e9e;
    border: black;
    border-width: 5px;
    border-radius: 0px
}


.signup-or-log-in input[type=email],
.signup-or-log-in input[type=password],
.signup-or-log-in input[type=text] {
    width: 100%;
    background-color: black;
    color: #fff;
}


.dark-theme .birthday-container .rbx-select-group .rbx-select,
.dark-theme .fake-input-lg {
    background-color: black;
    color: #fff;
    font-size: 16px;
    height: 38px;
    background-position-y: 5px;
    border-radius: 0px;
}


.container-footer {
    z-index: 3;
    background-color: #123f83 !important;
}

#nav-robux-icon .icon-robux-28x28 {
    background-position: 0px 0px;
    background-image: url(https://www.linkpicture.com/q/robuxtest.png);
}


#nav-robux-icon .icon-robux-28x28:hover {
    background-position: -28px 0px;
    background-image: url(https://www.linkpicture.com/q/robuxtest.png);
}


.light-theme .text-robux {
    color: #267F00;
    font-weight: bolder;
}



.icon-robux-price-container .icon-robux-16x16 {
    float: left;
    margin-right: 3px;
    margin-top: 1px;
    background-image: url(https://www.linkpicture.com/q/rbxtest_1.png);
}



.light-theme .text-robux-lg {
    color: #267F00;
    font-weight: bolder;
}


div#catalog #legend {
    padding: 18px 20px 5px 0;
}


.catalog-container .catalog-results .buy-robux {
    float: right;
    background-image: url(http://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    border-radius: 0px;
    border-color: #007001;
    background-color: #007001;
}




.light-theme .input-group .input-field {
    display: inline-block;
    padding-left: 38px;
    z-index: 0;
    border-right: 1px;
    background-image: url(http://www.roblox.com/images/StyleGuide/btn-control-large-tile.png);
    border-radius: 0px;
}



.light-theme .input-group-btn .input-dropdown-btn {
    background-image: url(http://www.roblox.com/images/StyleGuide/btn-control-large-tile.png);
    border-color: #606162;
    color: rgba(0, 0, 0, .6);
    border-radius: 0px;
}


.input-group-btn .dropdown-menu li a {
    padding: 10px 12px;
    display: block;
    background-image: url(http://www.roblox.com/images/StyleGuide/btn-control-large-tile.png);
}


.light-theme .item-card .item-card-thumb-container:before {
    border-radius: 0px;
    position: absolute;
    content: "";
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-image: conic-gradient(to right, rgba(255, 0, 0, 0), rgb(98, 98, 100, 1));
    transition: opacity .3s linear;
    opacity: 0;
}




.light-theme .btn-growth-lg {
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    border-radius: 0px;
    border-color: #007001;
    background-color: #007001;
    color: #fff;
}


.light-theme .btn-growth-lg:focus,
.light-theme .btn-growth-lg:hover {
    background-image: url(https://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    border-radius: 0px;
    border-color: #007001;
    background-color: #007001;
    color: #fff;
}

.btn-common-play-game-lg span[class^=icon-],
.btn-common-play-game-lg span[class^=icon-]:hover,
a.btn-common-play-game-lg span[class^=icon-],
a.btn-common-play-game-lg span[class^=icon-]:hover {
    background-size: 0px auto;
    width: 0px;
    height: 0px;
    object-position: center;
    position: fixed;
    background-color: #017501
}

.light-theme .btn-common-play-game-lg {
    background-image: url(http://www.roblox.com/images/Buttons/StyleGuide/bg-btn-green.png);
    border-color: black;
    color: #fff;
    border-radius: 0px;
    background-color: #017501
}



.light-theme .btn-common-play-game-lg::before {
    font-weight: bold;
    font-family: "Source Sans Pro";
    color: white;
    content: "PLAY ";
    font-size: 30px;
    text-align: center;
}

.light-theme .chat-container .chat-main .chat-header {
    background-color: #2570e1;
}





.light-theme .dialogs .dialog-container .dialog-message-container .dialog-message {
    background-color: #2570e1;
    border: 1px solid white;
    color: white;
}


.dialog-container .dialog-message-container .dialog-message span.message-piece {
    display: inline-block;
    max-width: 100%;
    color: white;
}


.font-header-2 .nav-menu-title .text-header::before {
    font-weight: bold;
    color: white;
    content: "Catalog";
}






.light-theme .dialogs .dialog-container .dialog-message-container.message-inbound .dialog-message {
    background-color: gray;
    color: white;
    border: 1px solid #c7cbce;
}




.light-theme .dialogs .dialog-container .dialog-header {
    background-color: #2570e1;
}




.search-options .search-options-header {
    background-image: url(https://web.archive.org/web/20140214101727im_/http://www.roblox.com/images/UI/catalog/btn-browse_categories.png);
    width: auto;
    height: 34px;
    display: block;
    cursor: pointer;
    font-size: 0;
}


.catalog-content .search-options .category-section {
    display: block;
    text-decoration: none;
    color: black;
    padding: 7px 6px;
    border: 1px solid gray;
    background: lightgray;
}

.light-theme .icon-default-branded-16x16,
.light-theme .icon-robux-16x16,
.light-theme .icon-robux-gray-16x16,
.light-theme .icon-robux-white-16x16 {
    background-image: url(https://www.linkpicture.com/q/robuxtest_2.png);
}

.light-theme .icon-default-navigation,
.light-theme .icon-nav-blog,
.light-theme .icon-nav-charactercustomizer,
.light-theme .icon-nav-friends,
.light-theme .icon-nav-giftcards,
.light-theme .icon-nav-group,
.light-theme .icon-nav-home,
.light-theme .icon-nav-inventory,
.light-theme .icon-nav-menu,
.light-theme .icon-nav-message,
.light-theme .icon-nav-my-feed,
.light-theme .icon-nav-notification-stream,
.light-theme .icon-nav-profile,
.light-theme .icon-nav-robux,
.light-theme .icon-nav-search-white,
.light-theme .icon-nav-settings,
.light-theme .icon-nav-shop,
.light-theme .icon-nav-trade {
    background-image: url(https://images.rbxcdn.com/53374db5b6c1b349a20d0471ea032868-navigation_dark.svg);
}
.btn-common-play-game-lg span[class^=icon-],
.btn-common-play-game-lg span[class^=icon-]:hover,
a.btn-common-play-game-lg span[class^=icon-],
a.btn-common-play-game-lg span[class^=icon-]:hover,
button.btn-common-play-game-lg span[class^=icon-],
button.btn-common-play-game-lg span[class^=icon-]:hover {
    background-size: 0px auto;
    width: 0px;
    height: 0px;
}


.light-theme .item-card-container .item-card-name {
    margin-bottom: 4px;
    max-height: 45px;
    height: auto;
    color: #123f83;
    font-weight: bold;
}

.strike-through {
    text-decoration: underline;
    color: #267F00;
    font-weight: bolder
}

.icon-limited-label {
    background-position: 0 -8px;
    background-image: url(https://www.roblox.com/images/star.png)
}

.light-theme .content {
    background-color: #ffffff;
    color: #393b3d;
}

.container-main {
    min-height: 60%;
    min-height: calc(100% - 183px);
    margin: 40px auto 0;
    position: relative;
    padding-bottom: 40px;
    background-color: #ffffff;
}



.icon-limited-unique-label {
    background: url(https://web.archive.org/web/20140214192323im_/http://images.rbxcdn.com/d649b9c54a08dcfa76131d123e7d8acc.png) no-repeat;
    background-position: left bottom
}

.icon-limited-label {
    background-position: left bottom;
    background-image: url(https://web.archive.org/web/20140214192323im_/http://images.rbxcdn.com/793dc1fd7562307165231ca2b960b19a.png);
}

.catalog-content .search-options .filters-header {
    margin: 0;
    padding: 12px 0 0;
    background-size: 0px
}
}
