css:
@-moz-document domain(netflix.com) {
/* Fix Small Screen Issues */
@media(max-width: 1200px) {
    /* Inactive Overlay */
    .evidence-overlay.nfa-z-idx-1.nfa-pos-abs.nfa-d-flex.nfa-w-100.nfa-h-100.nfa-flx-dir-col.nfa-bs-bb.nfa-jc-center {
    	width: 100%;
    	font-size: 2rem;
        padding: 5rem
    }
    .evidence-overlay.nfa-z-idx-1.nfa-pos-abs.nfa-d-flex.nfa-w-100.nfa-h-100.nfa-flx-dir-col.nfa-bs-bb.nfa-jc-center p:nth-of-type(1) {
    	width: 80%;
    }
    /* Control Icons */
    .PlayerControlsNeo__core-controls  {
        font-size: 1rem;
    }
}

/* Episode Title all in one line */
.PlayerControlsNeo__core-controls .ellipsize-text {
    display: flex;
}

/* Remove Forward + Backward buttons */
.touchable.PlayerControls--control-element.nfp-button-control.default-control-button.button-nfplayerFastForward,
.touchable.PlayerControls--control-element.nfp-button-control.default-control-button.button-nfplayerBackTen,
/* Remove Report Problem button */
.touchable.ReportAProblemPopupContainer.PlayerControls--control-element.nfp-popup-control,
/* Remove Playback Speed button */
.touchable[data-uia="speed-container"] {
    display: none;
}
}
