@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/*
 ======== Unread Inbox =======
*/


.tabbrowser-tab[label~="Gmail"][label^="Inbox ("] .tab-icon-image,
.tabbrowser-tab[label^="Gmail"][label*="Inbox ("] .tab-icon-image

{
padding-left: 16px;

background: url(data:image/x-icon;base64,) no-repeat !important;
}


.tabbrowser-tab[label~="Gmail"][label^="Inbox ("] .tab-text,
.tabbrowser-tab[label^="Gmail"][label*="Inbox ("] .tab-text
{
color:blue;
font-weight:bold;
}


