css:
@-moz-document url-prefix("https://uptobox"), url-prefix("https://uptostream"), url-prefix("http://uptobox"), url-prefix("http://uptostream") {
#content-wrapper {
    background: #181818;
}
.content-wrapper-upload {
    background: #181818 !important;
}
.upload-container {
    background: #212121;
}
#homepage .upload-container {
    background: #212121;
}
.upload-data .grid-row:nth-child(odd) {
    background: #212121;
}
.upload-data .grid-row {
    color: white;
}
#homepage .upload-container .upload-informations .progress-extended {
    color: white;
}
#uts-home .mainContent {
    background: #212121;
    color: white;
}
#uts-home .feature-container .feature {
    color: white;
}
.file-title {
    color: white;
}
#hls-video .recap-links, #uts-video .recap-links {
    color: white;
}
.files-wrapper .body .file-item .expanded-area {
    background: #212121;
    color : white;
}

.folder-container .section {
    background: #212121;
    color: white;
}
.folder-container label {
    color: white;
}
#footer {
    background: #212121;
}
.underline_title {
    color: white;
}
.bill-table .data-table {
    background: #212121;
    background-color: #212121;
}
#buy .payments .card-item {
    background: #212121;
}
.messaging.info {
    background: #212121;
}
.messaging {
    color: white;
}
.cell-padding {
    color: white;
}

.files-wrapper .body .file-item .main-area .name a {
    color: white;
}
.checkbox {
    color: white;
}

.files-wrapper .body .file-item.expanded, .files-wrapper .body .file-item.expanded:hover {
    background: #212121;
}
#myaccount .bill-table tr:not(.active):nth-child(odd) {
    background: #212121;
    color: white;
}
.data-table thead {
    background: #212121;
}
#myaccount .bill-table td {
    color: white;
}
.grayed-out-on-disabled.disabled .alert {
    color: white;
}
.comparison-table.col.three thead td:first-child {
    background: #212121;
}
.comparison-table thead td {
    background: #212121;
}
.comparison-table thead td:last-child {
    background: #252525;
    color: #e64141;
}
.comparison-table tr:nth-child(odd) {
    background: #212121;
    color:white;
}
.comparison-table tr:nth-child(2n) {
    background: #252525;
    color:white;
}
.comparison-table.col.three tbody td:nth-child(2), .comparison-table.col.three tbody td:nth-child(3) {
    background: #303030;
    color:white;
}
.comparison-table td:last-child {
    background: #353535;
    color:white;
}
.comparison-table tbody tr td:first-child {
    color: white;
}
#login .form-container {
    background: #252525;
}
#login .form-container .title {
    color: white;
}
#login .form-container .info {
    color: gainsboro;
}
#myaccount .info-icon {
    color: gainsboro;
}
#request-premium .description {
    color: gainsboro;
}
.folder-table tbody td {
    background: #252525;
    color: white;
}
#about-us {
    color: white;
}
 #reseller table td {
 	color: white;
}
#reseller table tr:first-of-type {
	background-color: #252525;  
}
#reseller table tr:nth-of-type(2) {
	background-color: #252525;  
}
.data-table.stripped tr:nth-child(odd) {
	background: #252525;
	color: white;
}
.data-table.stripped tr:nth-child(2n) {
	background: #252525;
	color: white;
}
.data-table tbody td {
    color: #ffffff;
}
}
