css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.anandtech.com") {

/* colors
white #FFFFFF
at blue #80A7C8
gray #555555
light gray #EEE9E9
*/

/* background colors */
body,.page { background:#80A7C8!important; }
.tcat, .thead, .tborder, .vbmenu_control, .tfoot { background:#003366!important; }
.alt1, .panelsurround, .panel { background:#DDDDDD!important; }
.alt2 { background:#CCCCCC!important; }

/* locks out custom font sizes in posts */
font { font-family:Verdana, Arial, Helvetica, sans-serif!important; font-size: 11px!important; }

/* remove bold from links*/
a:link { font-weight: normal!important; }
a:visited { font-weight: normal!important; }
strong { font-weight: normal!important; } 

/* smaller fonts, fusetalk style */
.smallerfont { font-family:Verdana,Lucida Sans,Lucida Grande,Arial!important; font-size:10px!important; }
td, th, p, li, .navbar { font-family:Verdana, Arial, Helvetica, sans-serif!important; font-size: 11px!important; }

/* shrink username in avatar panel */
.bigusername { font-weight:bold!important; font-size:12px!important; }

/* forum fixed width */
div[class='page'] { max-width:1100px!important; }
#posts div[align='left'] { padding-left:0px!important; padding-right:0px!important; }
#posts + div[align='center'] div[class='page'] div[align='left'] { padding-left:0px!important; padding-right:0px!important; }

/* center "contact us" line */
//td[class='tfoot'][align='right']{ text-align:center!important; }

/* border re-alignment */
div[id^='edit'] { padding-bottom:0px!important; }
.thead, .alt1, .alt2, .tborder { border:none!important; }

/* move jump menu to top right */
div[class='smallfont'] > form[action='forumdisplay.php'] { position:absolute!important; top:0!important; right:0!important; }

/* remove ugly icons */
img[src$="images/buttons/newthread.gif"] { display:none!important; }
img[src$="images/buttons/reply.gif"] { display:none!important; }
a[href^='newthread.php?do=newthread&f']:after { content: "NT"!important; }

/*
img[src$="images/buttons/quote.gif"] { display:none!important; }
img[src$="images/buttons/multiquote_off.gif"] { display:none!important; }
img[src$="images/buttons/quickreply.gif"] { display:none!important; }
img[src$="images/buttons/quickquote.gif"] { display:none!important; }
img[src$="images/buttons/edit.gif"] { display:none!important; }

a[href^='newreply.php?do=newreply&noquote']:after { content: "NR"!important; }
a[href^='editpost.php?do=editpost&p']:after { content: "ED"!important; }
a:not([onclick="return false"])[href^='newreply.php?do=newreply&p']:after { content: "QU"!important; }
a[onclick="return false"][href^='newreply.php?do=newreply&p']:after { content: "Q+"!important; }
a[id^="qr"][href^='newreply.php?do=newreply&noquote']:after { content: "QR"!important; }
a[href^="javascript:wog_qq"]:after { content: "QS"!important; }
*/

}
