css:
@-moz-document domain(forge.laravel.com) {
html.text-gray-800 {
  color: #a8bad3;
}
.bg-gray-100 {
  background-color: rgba(15 23 42);
}
nav.bg-white {
  background-color: rgb(15 23 42);
  border-bottom-width: 1px;
  border-color: rgb(248 250 252/.06);
}
.text-gray-500 {
  color: rgb(156 163 175);
}
.text-gray-600 {
  color: rgb(209 213 219);
}
.text-gray-700 {
  color: rgb(209 213 219);
}
.text-gray-800 {
  color: rgb(209 213 219);
}
.text-gray-900 {
  color: rgb(156 163 175);
}
.forge-tab-item {
  color: rgb(209 213 219);
}
.forge-h2, .forge-h3 {
  color: rgb(209 213 219);
}
.bg-teal-50.text-gray-800 {
  background-color: rgb(9 14 26);
}
.bg-white.rounded-lg {
  background-color: rgb(26 39 70);
}
.forge-input {
  color: rgb(178 194 217);
  background-color: rgb(15 23 42);
  border-color: rgb(51 65 85);
}
.rounded-lg.bg-blue-50 {
  background-color: rgb(21 32 58);
}
.forge-dropdown {
  background-color: rgb(15 23 42);
  border-color: rgb(51 65 85);
}
.forge-dropdown .border-b {
  border-color: rgb(51 65 85);
}
.forge-dropdown-item:hover {
  background-color: #090e1a;
}
.border-gray-100 {
  border-color: rgb(51 65 85);
}
.border-gray-200 {
  border-color: rgb(100 116 139);
}
.divide-gray-100>:not([hidden])~:not([hidden]) {
  border-color: rgb(51 65 85);
}
.forge-btn-secondary {
  background-color: rgb(9 14 26);
  background-image: none;
  color: rgb(209 213 219);
  border-color: rgb(51 65 85);
}
}
