css:
@-moz-document regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?."), regexp("https?://www.google.(com|([a-z]{2}))(.[a-z]{2})?/((\\?|webhp|search|gfe_rd|auth|gws_rd|#q|imghp|#gfe_rd|#safe|#pws|#tbs|#gws|#tbm).*)"), domain("google.co.jp") {
  :root{
    --MainColor: /*[[MainColor]]*/;
    --AccentColor: /*[[AccentColor]]*/;
  }
  body{
    background: url(/*[[Background]]*/) no-repeat fixed !important; 
    background-size: cover !important;
  }
  #hplogo, .A1P7Ob{
    display: none;
  }
  .RNNXgb, .o6juZc{	
    border: none !important;
    border-bottom: 1px solid var(--MainColor) !important;
    background: rgba(0, 0, 0, 0.5) !important;
    color: white !important;
  }
  .gLFyf {
    color: white !important;
  }
  .s8GCU {
    background-color: transparent !important;
  }
  #gb, .c93Gbe, .yg51vc, .appbar, .sfbg, .f6F9Be, #center_col, .jZWadf, .Lj9fsd, .aajZCb{
    background-color: rgba(0, 0, 0, .5) !important;
  }
  .gb_3{
    background-color: var(--AccentColor) !important;
    background-image: none;
    border-color: var(--AccentColor) !important;
  }
  .pHiOh, .uU7dJb, .gb_f, .hdtb-msel, .rQEFy, .fbar p, .fbar a, .AaVjTc a:link, .mJxzWe a, .GyAeWb a, .KwU3F, .xSQxL, .ynAwRc{
    color: var(--MainColor) !important;
  }
  cite{
    color: var(--AccentColor) !important;
  }

  .YTDezd{
    background-color:var(--MainColor);
  }
  .rQEFy{
    border-bottom: 3px solid var(--MainColor);
  }
}
