css:
@-moz-document domain("www.roblox.com"), domain("web.roblox.com"), domain("roblox.com") {
/* Make Background Transparent */
.content
  {
    background: transparent !important;
  }

/* CSS3 Snow Animation By Nicky Christensenn */
/* https://codepen.io/NickyCDK/pen/AIonk */
  /* Fixed by Tresha */
body{
    background: none;
	background-image: url('https://web.archive.org/web/20160309162207if_/http://www.wearewebstars.dk/codepen/img/s1.png'), url('https://web.archive.org/web/20160317073925if_/http://www.wearewebstars.dk/codepen/img/s2.png'), url('https://web.archive.org/web/20160616065451if_/http://www.wearewebstars.dk/codepen/img//s3.png');
	
	-webkit-animation: snow 10s linear infinite;
	-moz-animation: snow 10s linear infinite;
	-ms-animation: snow 10s linear infinite;
	animation: snow 10s linear infinite;
}

@keyframes snow {
  0% {background-position: 0px 0px, 0px 0px, 0px 0px;}
  50% {background-position: 500px 500px, 100px 200px, -100px 150px;}
  100% {background-position: 500px 1000px, 200px 400px, -100px 300px;}
}
@-moz-keyframes snow {
  0% {background-position: 0px 0px, 0px 0px, 0px 0px;}
  50% {background-position: 500px 500px, 100px 200px, -100px 150px;}
  100% {background-position: 400px 1000px, 200px 400px, 100px 300px;}
}
@-webkit-keyframes snow {
  0% {background-position: 0px 0px, 0px 0px, 0px 0px;}
  50% {background-position: 500px 500px, 100px 200px, -100px 150px;}
  100% {background-position: 500px 1000px, 200px 400px, -100px 300px;}
}
@-ms-keyframes snow {
  0% {background-position: 0px 0px, 0px 0px, 0px 0px;}
  50% {background-position: 500px 500px, 100px 200px, -100px 150px;}
  100% {background-position: 500px 1000px, 200px 400px, -100px 300px;}
}
}
