css:
@-moz-document regexp("(.*www\\.luogu\\.com\\.cn/|.*www\\.luogu\\.com\\.cn/[^b].*)") {
.lfe-body {
    background: #282828 !important;
}
.header-layout.tiny[data-v-90bffe18] {
    background: #202020 !important;
}
.lg-article, .card, .am-panel, .am-g.lg-table-bg0.lg-table-row, button, .am-pagination>li>a, .am-pagination>li>span, .drop, .image-block, .user-nav, .mp-editor-container, .inner-card, .discuss, .message-block > .message, .list > .item, .notice-card.notice-card {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
}

.notice-card.notice-card {
    margin-bottom: 5px;
    margin-top: 5px;
}

.card-row > .title {
    margin-left: 10px !important;
    color: white !important;
}

.dropdown {
    background: rgb(61, 61, 61) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 8px 16px rgba(0, 0, 0, 0.26) !important;
    border-radius: 8px !important;
    color: white !important;
}
.test-case {
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
}
.user-nav {
    padding: 5px;
}
.am-pagination>.am-active>a, .am-pagination>.am-active>a:focus, .am-pagination>.am-active>a:hover, .am-pagination>.am-active>span, .am-pagination>.am-active>span:focus, .am-pagination>.am-active>span:hover {
    background: #0e90d2 !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
}
.bottom.float-bottom, .solution-article > .operations.float {
    background: #383838 !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
}
input.am-form-field, textarea#feed-content, input.lfe-form-sz-middle, input.lfe-form-sz-small, .swal2-input, textarea.lfe-form-sz-middle {
    background: rgba(255, 255, 255, 0.0605) !important;
    border-radius: 5px !important;
    border-color: rgba(0, 0, 0, 0.06) rgba(0, 0, 0, 0.06) rgba(255, 255, 255, 0.5442) rgba(0, 0, 0, 0.06) !important;
    border-width: 1px 1px 1px 1px !important;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.04) !important;
    color: white !important;
}
input.am-form-field:focus, textarea#feed-content:focus, input.lfe-form-sz-middle:focus, input.lfe-form-sz-small:focus, .swal2-input:focus, textarea.lfe-form-sz-middle:focus {
    background: rgba(255, 255, 255, 0.0605) !important;
    border-radius: 5px !important;
    border-color: rgba(0, 0, 0, 0.06) rgba(0, 0, 0, 0.06) rgba(96, 205, 255, 1) rgba(0, 0, 0, 0.06) !important;
    border-width: 1px 1px 2px 1px !important;
    border-bottom-width: 2px !important;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.04) !important;
    color: white !important;
}
.svg-inline--fa, tspan, text {
    fill: white !important;
}
.highcharts-axis-labels.highcharts-xaxis-labels > text {
    fill: rgba(255, 255, 255, 0.5) !important;
}
.highcharts-grid-line, .message::after, .message::before {
    display: none;
}
.highcharts-point.highcharts-color-1 {
    fill: #FFFFFF !important;
    stroke: #606060;
}
.highcharts-point.highcharts-color-0 {
    fill: rgba(0, 120, 212) !important;
}
.highcharts-graph {
    stroke: rgba(0, 120, 212) !important;
}
.highcharts-area {
    fill: rgba(0, 120, 212, 0.5) !important;
}
.header-link, .user-nav {
    color: rgba(255, 255, 255, 0.8) !important;
}
.am-btn {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 5px !important;
    color: white !important;
}
.am-btn:hover {
    background: rgba(255, 255, 255, 0.08) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 5px !important;
    color: white !important;
}
.am-btn:focus {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 5px !important;
    color: white !important;
    outline: 0 !important;
}
.am-btn.am-disabled {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 5px !important;
    color: white !important;
    transition: background-color 100ms ease-out, border-color 100ms ease-out;
}
.am-btn.am-disabled:focus {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 5px !important;
    color: white !important;
    transition: background-color 100ms ease-out, border-color 100ms ease-out;
}
select.am-form-field, .text.lfe-form-sz-small, .text.lfe-form-sz-middle {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
    transition: background-color 100ms ease-out, border-color 100ms ease-out;
}
select.am-form-field:hover, .text.lfe-form-sz-small:hover, .text.lfe-form-sz-middle:hover {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
}
select.am-form-field:focus, .text.lfe-form-sz-small:focus {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
    outline: 0 !important;
}
.am-comment-main, .am-comment-hd {
    border-radius: 8px;
}
.am-comment-main {
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
}
.am-comment-bd {
    background: rgba(255, 255, 255, 0.0512) !important;
    color: #ffffff !important;
    border-radius: 0px 0px 8px 8px;
}
.am-comment-actions, .am-comment-meta, .mp-editor-toolbar, .cover-upload {
    background: rgb(61 61 61) !important;
    color : #ffffff !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 7px 7px 0px 0px;
}
.am-comment-main:before, .am-comment-main:after {
    display: none;
}
.am-comment-primary .am-comment-avatar {
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
}
.lg-small, .result-count, .detail.lfe-caption, .expand-tip > span, .radio-group > *, .meta, .info-content, .lfe-caption, time.time {
    color: rgba(255, 255, 255, 0.6) !important;
    fill: rgba(255, 255, 255, 0.6) !important;
}
.light-black {
    color: rgba(255, 255, 255, 0.9) !important;
}
.number, #swal2-content, .swal2-popup .swal2-title, .section>.title, p {
    color: #ffffff !important;
}
.row:not(:last-child), .header-wrap {
    border-color: rgba(255, 255, 255, 0.4) !important;
}
.progress-frame {
    background: rgb(61, 61, 61) !important;
    border-color: rgb(61, 61, 61) !important;
}
strong, .header > .right a {
    color: rgb(255, 255, 255) !important;
}
.mp-dialog-container, .swal2-popup, .center {
    background: rgb(51, 51, 51) !important;
    border-color: rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
}
.refined-input.input-wrap.frame {
    background: none;
    border: none;
}

.side > div {
    border: none !important;
}
blockquote {
    border-left: 5px solid rgba(255, 255, 255, 0.2) !important;
}
pre {
    border: rgba(255, 255, 255, 0.1) solid 1px !important;
    background: rgba(255, 255, 255, 0.1) !important;
}
.marked h1, .marked h, .mp-preview-content h1, .mp-preview-content h2, .editor, .side, .top-container,
.container>.tag-select-area .section, .container>.menu-container, .notice-header {
    border-color: rgba(255, 255, 255, 0.2) !important;
}
.svg-inline--fa.fa-angle-down.fa-w-10 path {
    fill: #ffffff !important;
}
.expand {
    background: linear-gradient(rgba(255, 255, 255, 0), rgb(51, 51, 51)) !important;
}
.tags > .lfe-caption.tag {
    background: rgba(255, 255, 255, 0.1) !important;
}

g.highcharts-label.highcharts-tooltip.highcharts-color-0 > path:nth-child(4) {
    fill: rgba(61,61,61,0.95) !important;
}

.am-nav>li.am-active>a, .am-nav>li>a:focus {
    background: rgba(255, 255, 255, 0.0512) !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
}

.am-nav>li>a:hover {
    background: none;
}
}

@-moz-document regexp("(.*www\\.luogu\\.com\\.cn/|.*www\\.luogu\\.com\\.cn/[^b].*)") {
code {
    border: rgba(255, 255, 255, 0.1) solid 1px !important;
    background: rgba(255, 255, 255, 0.1) !important;
    border-radius: 3px;
    color: #EB5757;
}
.ace_editor {
    font: 16px/normal'Menlo', 'Ubuntu Mono', 'Consolas', 'source-code-pro', 'Monaco', monospace !important;
}
.ace-clouds .ace_gutter-active-line, .ace-clouds .ace_marker-layer .ace_active-line {
    background: rgba(255, 255, 255, 0.1) !important;
}
.ace-clouds {
    background: #1E1E1E !important;
    border: 1px solid rgba(0, 0, 0, 0.2) !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border-radius: 8px !important;
    color: white !important;
}
.ace-clouds .ace_gutter {
    background: rgba(255, 255, 255, 0.0512) !important;
    color: rgba(255, 255, 255, 0.9) !important;
}
.ace-clouds .ace_keyword, .ace-clouds .ace_meta, .ace-clouds .ace_support.ace_constant.ace_property-value {
    color: #B77DB2;
}
.ace-clouds .ace_keyword.ace_operator {
    color: #DCDCAA;
}
.ace_identifier {
    color: #9CDCFE;
}
.ace-clouds .ace_storage, .ace-clouds .ace_support.ace_class, .ace-clouds .ace_support.ace_function, .ace-clouds .ace_support.ace_other, .ace-clouds .ace_support.ace_type {
    color: #569CD6;
}
.ace-clouds .ace_constant.ace_numeric {
    color: #B5CEA8;
}
.ace-clouds .ace_string {
    color: #CE9178;
}
.ace-clouds .ace_cursor {
    color: #FFFFFF;
}
.ace-clouds .ace_comment {
    color: #6A9955;
}
.ace-clouds .ace_indent-guide {
    background: none;
    border-right: 1px solid rgb(64, 64, 64) !important;
}
}

@-moz-document regexp("(.*www\\.luogu\\.com\\.cn/|.*www\\.luogu\\.com\\.cn/[^b].*)") {
.CodeMirror-gutters {
    background: rgb(61 61 61) !important;
    color : #ffffff !important;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.13) !important;
    border: none;
}
.mp-editor-ground {
    border: none !important;
}
.mp-preview-area, .CodeMirror {
    background: none !important;
}
.CodeMirror, .mp-preview-content, .mp-editor-menu>li>a {
    color : #ffffff !important;
    border: none !important;
}
.CodeMirror pre {
    background: none !important;
    border: none !important;
}
.cm-s-default .cm-header {
    color: #569CD6;
}
.cm-s-default .cm-comment {
    color: #CE9178;
}
.cm-s-default .cm-link {
    color: #B6CEA8;
}
.cm-s-default .cm-string {
    color: #4EC9B0;
}
.cm-s-default .cm-variable-2 {
    color: #9DDCFE;
}
pre > code {
    color: #FFFFFF !important;
    border: none !important;
    background: none !important;
}
}
