/* ==UserStyle==
@name        Dark svtv.org userstyle
@namespace   Mayurifag
@version     3.0.0
@homepageURL https://github.com/Mayurifag/dark-svtv-org-userstyle
@updateURL   https://raw.githubusercontent.com/Mayurifag/dark-svtv-org-userstyle/main/dark-svtv-org.user.css
@license     MIT
@author      Mayurifag
@preprocessor uso
==/UserStyle== */
@-moz-document domain("svtv.org") {
  :root {
    --background-primary: #000;
    --background-secondary: #1e1c1c;
    --background-3: #282828;
    --text-primary: #fff;
    --text-secondary: #939393;
    --accent: #fed27a;
    --thread: #f7931a;
    --opinions: #7aefff;
    --translation: #2e8eff;
    --selection-background: rgba(255 187 66 27%);
    --backward-cards-background-color: hsla(0 0% 100% 12%);
  }
  html {
    background-color: var(--background-primary);
  }
  .page__content-wrap {
    background-color: var(--background-primary);
  }
  .card-news__title {
    color: var(--text-primary);
    background-color: inherit;
  }
  .block-cards__button {
    background-color: var(--accent);
    color: var(--background-secondary);
  }
  .post-fullpage__title {
    color: var(--text-primary);
  }
  .block-cards__title {
    color: var(--text-primary);
  }
  .card-news__category {
    color: var(--accent);
    background-color: inherit;
  }
  .card-news__content {
    background-color: var(--background-secondary);
  }
  .card-thread__content {
    background-color: var(--background-secondary);
  }
  .card-thread__category {
    color: var(--thread);
    background-color: inherit;
  }
  .post-fullpage__title-info {
    background-color: var(--background-secondary);
  }
  .thread-blob {
    background-color: var(--background-3);
  }
  .thread-blob__date {
    background-color: var(--background-3);
    color: var(--accent);
    font-weight: 400;
    opacity: 0.9;
  }
  .card-opinions__text {
    background-color: var(--background-3);
  }
  .card-opinions__category {
    background-color: var(--background-3);
    color: var(--opinions);
  }
  .card-opinions__person-name {
    background-color: var(--background-3);
  }
  .card-opinions__date {
    background-color: var(--background-3);
  }
  .block-cards__item-width-1-5 {
    color: var(--background-3);
    background-color: inherit;
  }
  .card-thread__title {
    background-color: inherit;
    color: var(--text-primary);
  }
  .banner__navigation-text {
    background-color: inherit;
  }
  .banner__navigation-date {
    background-color: inherit;
  }
  .sb-ad-banner {
    display: none;
  }
  .sb-footer__text {
    background-color: var(--background-secondary);
    color: var(--text-primary);
  }
  .sb-footer {
    background-color: var(--background-secondary);
    color: var(--text-primary);
    border-color: var(--background-secondary) !important;
  }
  .card-news {
    background-color: inherit;
    border-color: var(--background-secondary);
  }
  .card-news__date {
    color: var(--text-secondary);
  }
  .page__footer-wrap {
    box-shadow: inherit;
  }
  .post-donation__header {
    background-color: var(--background-secondary);
  }
  .post-donation__crypto-name {
    background-color: var(--background-secondary);
  }
  .card-thread__date {
    color: var(--text-secondary);
  }
  .post-fullpage__content p {
    background-color: var(--background-secondary);
    color: var(--text-primary);
  }
  .post-fullpage__content {
    background-color: var(--background-secondary) !important;
    color: var(--text-primary);
  }
  .author-header__name {
    color: var(--text-primary);
  }
  .author-header__description {
    color: var(--text-secondary);
  }
  .thread-blob p {
    background-color: var(--background-3);
  }
  .thread-blob::after {
    background-color: var(--background-3);
  }
  /* 404 page */
  .container__title {
    color: var(--text-primary);
  }
  /* some pages have no donation footer actually, but have wrong footer div */
  .author-template .page__footer-wrap {
    background-color: inherit;
  }
  .page--is-post-donation .page__footer-wrap {
    background-color: inherit;
  }
  .post-card-notice--tor .post-card-notice__description {
    background-color: inherit !important;
  }
  .post-card-notice--red-shield {
    background-color: var(--background-3);
  }
  .post-card-notice--red-shield .post-card-notice__description {
    background-color: var(--background-3);
  }
  .sb-currency__items {
    background-color: var(--background-secondary);
  }
  .sb-currency {
    background-color: var(--background-secondary);
    border-color: var(--background-secondary) !important;
  }
  .sb-currency__value {
    color: var(--text-primary);
  }
  .sb-currency__token {
    color: var(--text-primary);
  }
  /* xd nice color */
  .sb-currency__token--btc {
    color: var(--thread);
    opacity: 1;
  }
  .sb-currency__arrow {
    vertical-align: inherit;
  }
  .post-jobs-item {
    background-color: var(--background-3);
  }
  .post-jobs-item__paragraph {
    background-color: var(--background-3) !important;
  }
  /* 'Online' sidebar page and block */
  .tag-online .page__content-wrap {
    background: inherit;
  }
  .online__message-text {
    background: var(--background-secondary);
    color: var(--text-primary);
  }
  .block-cards__item {
    background: var(--background-secondary) !important;
  }
  .online__message-time {
    color: var(--text-secondary);
  }
  .sb-online {
    background: var(--background-secondary) !important;
    color: var(--text-primary);
    border-color: var(--background-secondary) !important;
  }
  .sb-online__show-all-button {
    background-color: var(--background-3);
    color: var(--thread);
  }
  .sb-online__show-all-button::selection {
    background-color: var(--selection-background);
  }
  .sb-online__item {
    background: var(--background-secondary);
    color: var(--text-primary);
  }
  .sb-online__item:hover {
    background-color: var(--background-3);
  }
  .post-fullpage__content.page-gas {
    background-color: transparent !important;
  }
  /* That looks stupid. I should make this semi-transparent or something */
  .page-gas__social-list {
    background: var(--background-secondary);
  }
  .page-gas__social {
    background: var(--background-secondary);
  }
  .page-gas__social-text {
    color: var(--text-secondary);
  }
  a .sb-footer__header span {
    color: var(--text-primary);
  }
  .slide-thread__content {
    background-color: var(--background-3);
    color: var(--text-primary);
  }
  .slide-thread::after,
  .slide-thread::before {
    background-color: var(--background-3) !important;
    border-color: var(--backward-cards-background-color);
  }
  .slide-thread__content__footer {
    color: var(--text-primary);
    opacity: 0.5;
  }
}
