css:
@namespace url(http://www.w3.org/1999/xhtml);


/* 検索用
******************************************************/
@-moz-document domain("p2.2ch.net") {}


/* リスト
******************************************************/
@-moz-document url-prefix("http://p2.2ch.net/p2/subject.php") {

#sbtoolbar1 + table > tbody > tr:hover {
	background-color: #ffcccc !important;
}
#sbtoolbar1 + table > tbody >  tr:hover * {
	background-color: transparent !important;
}
#sbtoolbar1 + table > tbody >  tr > td  > a {
	display:block;
	width: auto !important;
}
}/* @-moz-document End */


/* スレ
******************************************************/
@-moz-document url-prefix("http://p2.2ch.net/p2/read.php?"),
               url-prefix("http://p2.2ch.net/p2/read_new.php?") {

body {
	background-color: #aaaaaa !important;
}
dl {
	margin-left: 0px !important;
	margin-right: 0px !important;
}

dl > dt[id^="r"] {
	margin-top: 15px !important;
	border-radius: 10px 10px 0px 0px;
	background-color: rgb(239, 239, 239);

	padding-left: 15px !important;
	padding-top: 10px !important;
	padding-bottom: 5px !important;
	box-shadow: 0pt 4px 4px rgba(0, 0, 0, 0.5),
		inset 2px 2pt 2px rgba(255, 255, 255, 0.5);
}
dl > dt[id^="r"] ~ dd:not([id^="q"]) {
	border-radius: 0px 0px 10px 10px;
	background-color: rgb(239, 239, 239);
	margin-left: auto !important;
	padding-left: 45px !important;
	box-shadow: 0pt 4px 4px rgba(0, 0, 0, 0.5),
		inset 2px -2pt 2px rgba(255, 255, 255, 0.5);
}
}/* @-moz-document End */
