@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document   url-prefix(https://www.google.com/voice)
{
a:link, a:visited, a:hover, a:active {color: #CCFF55 !important;}

/*** Header ***/
#gbg5, .gc-searchbar, .goog-flat-menu-button.goog-flat-menu-button-hover, .goog-flat-menu-button.goog-flat-menu-button-open
 {background-color: transparent !important; background-image: none !important; border-color: transparent !important}
#gc-header, #gbx2, #gbx3, #gbx4, .gbgt:focus, .gbzt:focus, .gbgtd:focus, .gbto .gbts, .gbzt, .gbgt, .gbgtd, .gbmc
 {background-color:#000 !important; }
.gbzt:hover, .gbgt:hover, .gbgtd:hover, .gbmt:hover
 {background-color: #2A2A2A !important}
.gbzt, .gbgt, .gbgtd, #gbx1, #gbx3, #gbx4, .gbtcb, .gbto .gbts, .gbtb2, .gbmh, .gbm
 {border-color: #063 !important}
#gbx2
 {border-color: transparent !important}
/* Navigation Header */
#gc-header-did-link, .gbto .gbts.gc-message-bg-t
 {color: #006633 !important;}
#gbz .gbzt, #gbz .gbgt, #gbg .gbgt, .gbmtc .gbmt, .gbto .gbts
 {color: #4DB339 !important}
.gbtb
 {display: none !important}
.gc-searchbar, .gc-inbox-sidebar-header, .gc-inbox-view-header
 {border-color: #2A2A2A !important}

/*** Alerts ***/
/* Loading announcement */
.gc-rounded-mm
 {color: #CF5 !important; background-color: #2A2A2A !important;  -moz-border-radius: 4px !important; -moz-border-radius-bottomright: 4px !important; padding: 6px !important;}
.gc-rounded-ml, .gc-rounded-mr, .gc-rounded-bl, .gc-rounded-br, .gc-rounded-tl, .gc-rounded-tr 
 {display: none !important;}
/* Sent Notification */
.gc-yellow-rnd .gc-rounded-bg
 {background-color: #2A2A2A !important;}
.gc-yellow-rnd table, .gc-inbox-search-banner
 {color: #006633 !important;}
/* Popup Content */
.gc-help {background-color: #2A2A2A !important; -moz-border-radius: 2px !important; border:1px solid #2A2A2A !important;}

/* Search Bar */
input.gc-text, textarea.gc-text, select.gc-text, #gc-search-btn, .goog-inline-block > textarea, .gc-message-sms-box, .gc-message-checkbox 
{border-color: #CCFF55 !important; opacity:0.8 !important;}

/*** Main Interface ***/
body {background: #000 !important;}
body, .gc-inbox-sidebar-item {color: #4DB339 !important;}
.gc-inbox-sidebar-item.goog-option-selected, .gc-inbox-sidebar-subitem.goog-option-selected 
{color: #006633 !important; font-weight:bold !important;}
.goog-option-selected {background: transparent none repeat scroll 0 0 !important;}
#gc-content {background-color: #000 !important;}
.gc-message {border-color: transparent !important}
/* Menu Dividers */
.gc-inbox-sidebar-header, .gc-inbox-view-header, .goog-splitpane-handle 
 {background-color: #2A2A2A !important;}
#gc-contacts-sidebar-menu, #gc-inbox-sidebar-menu > div
{height: 22px !important}
.gc-inbox-sidebar-item, .sectionDivider
{border: 1px transparent !important}

/***      Menus      ***/
.gc-inbox-sidebar-header
{padding-top: 12px !important; margin-top: 0 !important}
.gc-inbox-sidebar-menu {border-style: none !important; background: transparent !important;}
.gc-simple-menu2 .goog-flat-menu-button, .gc-simple-menu .goog-flat-menu-button, .gc-control
 {color: #CF5 !important;}
.goog-menuitem-highlight, .goog-flat-menu-button.goog-flat-menu-button-hover, .goog-flat-menu-button.goog-flat-menu-button-open
{background: #2A2A2A !important; border: transparent !important}
.goog-menuitem-highlight > div, .goog-flat-menu-button.goog-flat-menu-button-hover, .goog-flat-menu-button.goog-flat-menu-button-open, .phoneNumber:hover
{color: #063 !important; font-weight: bold !important}
.goog-menuitem-highlight, .goog-menuitem-hover
{padding-bottom: 6px !important; padding-top: 6px !important}
.gc-inbox-sidebar-menu, .gc-inbox-sidebar-types-header, .gc-inbox-sidebar-types-menu, .gc-sidebar-contact-menu {background: transparent !important;}

.goog-splitpane-second-container
{background-color: #2A2A2A !important}
#gc-view-main
{background-color: black !important}

/**      Sidebar      **/
.gc-inbox-sidebar-main
{padding: 0 !important; margin: 0 !important}
.gc-inbox-sidebar-main > div
{padding-left: 22px !important}
.gc-inbox-sidebar-main > div:first-child
{padding-top: 16px !important}
.gc-sidebar-contact-item {color: #CF5 !important;}
.gc-inbox-sidebar-sections
{padding: 0 !important; margin: 0 !important}
#gc-inbox-sidebar-main
{background-color: black !important; border-right: 4px solid #2A2A2A !important}
selected, .gc-inbox-sidebar-subitem.goog-option-selected, .gc-inbox-sidebar-main .goog-option-selected .goog-menuitem-content
{color: #063 !important}

/***      Fonts      ***/
.gc-message-sms-from, .gc-message-sms-text {color: #006633 !important;}
.gc-message-name, .gc-message-time, .gc-message-more-menu .goog-flat-menu-button-caption
 {color: #CF5 !important;}
.gc-inbox-view-main, .gc-message-read, .gc-message-bg-msg, .gc-message-sms-actions
 {background-color: transparent !important;}
/*** Callout arrows for messages
.gc-message-bg-callout
 {border-color: transparent #2A2A2A transparent transparent !important;
  border-style: solid !important;
  border-width: 10px 15px 10px 10px !important;
  height:0 !important;
  width:0 !important;
  background: none !important;
  position:absolute !important;
} New style swaps arrows for bg color change*/
.gc-message-bg-f > div:first-child
  {padding-left: 8px !important}

/*** Message List ***/
.gc-message-sline 
{border-top: 5px black !important; border-radius: 5px 0 0 0 !important}
.gc-message-read .gc-message-sline {background: #006633 !important}
.gc-sline-top, .gc-sline-bottom
{display: none !important}
td.gc-message-bg-m
  {background-color: #2A2A2A !important; }
.gc-message-bg-upper
  {border-radius: 6px 6px 0 0 !important}
.gc-message-bg-g
  {border-radius: 0 0 6px 6px !important}
.gc-message-bg-l, .gc-message-bg-b, .gc-message-bg-r, .gc-message-bg-tl, .gc-message-bg-t, .gc-message-bg-tr
  {background: transparent !important;}
.gc-message {background: transparent !important;}


/***     SMS Menu    ***/
.ac-renderer div.active {background-color: #000 !important;}
.ac-highlighted {color: #006633 !important;}
.gc-sms-counter {color: #CF5!important;}
.gc-bubble-callout {
  border-color: transparent transparent #006633 transparent !important;
  border-style: solid !important;
  border-width: 10px 7px 10px 7px !important;
  height:0 !important;
  width:0 !important;
  background: none !important;
  position:absolute !important;
  top: -8px !important;}
.goog-menu
 {color: #4DB339 !important; background-color: #000 !important; border-color: #063 !important;}
.gc-bubble-mc {background: #2A2A2A !important;}
.gc-bubble-ml, .gc-bubble-mr 
  {background: #006633 !important; width: 5px !important;}
.gc-bubble-tl, .gc-bubble-bl
  {background: #006633 !important; height: 5px !important;}
.gc-bubble-tr, .gc-bubble-br
  {background: #006633 !important; height: 5px !important; width: 5px !important;}
/*** Borders & Corners ***/
.gc-bubble-tl, td.gc-message-bg-tl {-moz-border-radius-topleft:6px !important;}
.gc-bubble-bl, td.gc-message-bg-bl {-moz-border-radius-bottomleft: 6px !important;}
.gc-bubble-tr, td.gc-message-bg-tr {-moz-border-radius-topright: 6px !important;}
.gc-bubble-br, td.gc-message-bg-br {-moz-border-radius-bottomright: 6px !important;}
.goog-bubble-close-button 
  {top: 1px !important; left: -15px !important; background-color: #2A2A2A !important; opacity: 0.8 !important; -moz-border-radius:5px !important;}
.gc-appbar,
.gc-inbox-sidebar-menu-border-top, .gc-inbox-sidebar-block {border-color: #2A2A2A !important;}

/*** Image Replacements ***/
/*Calling Credit Help Icon*/
img[src*="/resources/109158364-help_dark.gif"] {width:0px !important; height: 15px !important; padding-left: 15px !important; background: transparent url(data:image/png;base64,) no-repeat scroll !important;}
[alt="Page-right"] 
{background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABEAAAAPCAYAAAACsSQRAAAANklEQVR42mNgAILQM4L/ycUMlBoAN2hoGAICVDGEGIOIMoSQQUQbgs8g+rlk4GNnCKZYauRiAKemQtpD0btkAAAAAElFTkSuQmCC);
 width: 0px !important; height: 0px !important; padding-top: 15px !important; padding-left: 17px !important;}
[alt="Page-right-end"]
{background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABEAAAAPCAYAAAACsSQRAAAAOklEQVR42mNgAILQM4L/ycUMlBoAN2hwGwICxIgRNARdE9mGIGukyBCY5oF1CcVhQnHsDOEUS41cDACNtk9n2jafXwAAAABJRU5ErkJggg==);
 width: 0px !important; height: 0px !important; padding-top: 15px !important; padding-left: 17px !important;}
[alt="Page-left"]
{background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABEAAAAPCAYAAAACsSQRAAAANklEQVR42mNgAILQM4L/ycUMlBoAN2hoGQICFBkCA2QbggzIMgQdDJxLqBYmVIudIZZiqZGLAYr2QtrqYndGAAAAAElFTkSuQmCC);
 width: 0px !important; height: 0px !important; padding-top: 15px !important; padding-left: 17px !important;}
[alt="Page-left-end"]
{background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABEAAAAPCAYAAAACsSQRAAAAOUlEQVR42mNgAILQM4L/ycUMlBoAN2jwGwICxIiRZAgMkG0IMiDLEHQwcC6hWpiQHTtDNMVSIxcDAG7HT2eyD2NzAAAAAElFTkSuQmCC);
 width: 0px !important; height: 0px !important; padding-top: 15px !important; padding-left: 17px !important;}
.gc-message-star-off
{background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAOCAYAAAAmL5yKAAAAi0lEQVR42mNgwAFW/U/7D8IM5IBnH///hxkAYpNswLH7CE1H75NhALLTCXoD5lR0DJM/cOf/fxBGlrv5CslVu2/+Jymw7r7Bon7/HeIMwRszIIkTD3AHGFHRiksRSOzQXSJiAxQexMQMUbYT4hOMe2xRSpQBIPrqi/8omvbf/v+fKANwORM5b6DLAQDGh810SDf2swAAAABJRU5ErkJggg==) !important;}
.gc-message-star-on
{background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAOCAYAAAAmL5yKAAAAXUlEQVR42mNgwAFW/U/7D8IM5AKKDABpnDlzAhiTZQhJBsCcio6RDcCGMQyAaSCEsYYNsYbgDVhChhAVK7gMITpKCbmA5OgjOTpxRR1RhsAUovsX2RCCBuBTgEseAH7eOu7gIcVHAAAAAElFTkSuQmCC) !important;}
.gc-inbox-sidebar-star div.goog-menuitem-checkbox
 {background: none !important}
.goog-zippy-expanded
 {background: url(data:image/png;base64,)
 no-repeat scroll !important;}
.goog-zippy-expanded:hover
 {background-position: 0 -36px !important;}
.goog-zippy-collapsed
 {background: url(data:image/png;base64,)
 no-repeat scroll 0 -12px !important;}
.goog-zippy-collapsed:hover
 {background-position: 0 -48px !important;}
}
