/* vBulletin 3 CSS For Style 'DLP v2' (styleid: 2) */

@namespace url(http://www.w3.org/1999/xhtml) !important;

@-moz-document domain("darklordpotter.net") {

body
{
	background: grey !important;
	color: #93BE91 !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 0px auto 0px auto !important;
	padding: 0px 8px 0px 8px !important;
	border: solid #223B21 !important;
	border-width: 0px 1px 0px 1px !important;
	min-width: 780px !important;
	max-width: 1024px !important;
}
a:link, body_alink
{
	color: #93BE91 !important;
}
a:visited, body_avisited
{
	color: #93BE91 !important;
}
a:hover, a:active, body_ahover
{
	color: #38D238 !important;
}
.page
{
	background: grey !important;
	color: #93BE91 !important;
}
td, th, p, li
{
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tborder
{
	color: #93BE91 !important;
	border: solid #223B21 !important;
	border-width: 1px !important;
	
}
.tcat
{
	background: #0A1C0B !important;
	color: #809775 !important;
	font: bold 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tcat a:link, .tcat_alink
{
	color: #809775 !important;
	text-decoration: none !important;
}
.tcat a:visited, .tcat_avisited
{
	color: #809775 !important;
	text-decoration: none !important;
}
.tcat a:hover, .tcat a:active, .tcat_ahover
{
	color: #ACC0AB !important;
	text-decoration: underline !important;
}
.thead
{
	background: #666666 !important;
	color: #809775 !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.thead a:link, .thead_alink
{
	color: #809775 !important;
}
.thead a:visited, .thead_avisited
{
	color: #809775 !important;
}
.thead a:hover, .thead a:active, .thead_ahover
{
	color: #ACC0AB !important;
}
.tfoot
{
	background: #0A1C0B !important;
	color: #809775 !important;
	border: solid #223B21 !important;
	border-width: 1px 0px 1px 0px !important;
}
.tfoot a:link, .tfoot_alink
{
	color: #6A7E67 !important;
}
.tfoot a:visited, .tfoot_avisited
{
	color: #6A7E67 !important;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover
{
	color: #93AE95 !important;
}
.alt1, .alt1Active
{
	background: #555555 !important;
	color: #BAD6C2 !important;
}
.alt2, .alt2Active
{
	background: #666666 !important;
	color: #BAD6C2 !important;
}
td.inlinemod
{
	background: #0A1C0B !important;
	color: #BAD6C2 !important;
}
.wysiwyg
{
	background: #21301F !important;
	color: #BAD6C2 !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
textarea, .bginput
{
	background: #000000 !important;
	color: #BAD6C2 !important;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 0px 4px 0px 4px !important;
	border: solid #666666 !important;
	border-width: 1px !important;
}
.button
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 0px 4px 0px 4px !important;
}
select
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 0px 4px 0px 4px !important;
}
option, optgroup
{
	font-size: 11px !important;
	font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.smallfont
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.time
{
	color: #AAA6A2 !important;
}
.navbar
{
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.highlight
{
	background: #3E6439 !important;
	color: #BAD6C2 !important;
	font-weight: bold !important;
}
.fjsel
{
	background: #BAD6C2 !important;
	color: #21301F !important;
}
.fjdpth0
{
	background: #21301F !important;
	color: #BAD6C2 !important;
}
.panel
{
	background: #21301F !important;
	color: #BAD6C2 !important;
	padding: 5px !important;
	border: 1px solid #223B21 !important;
}
.panelsurround
{
	background: #0A1C0B !important;
	color: #BAD6C2 !important;
}
legend
{
	background: #21301F !important;
	color: #BAD6C2 !important;
	font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.vbmenu_control
{
	background: #0A1C0B !important;
	color: #BAD6C2 !important;
	font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	padding: 3px 6px 3px 6px !important;
	white-space: nowrap !important;
}
.vbmenu_control a:link, .vbmenu_control_alink
{
	color: #BAD6C2 !important;
	text-decoration: none !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited
{
	color: #BAD6C2 !important;
	text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover
{
	color: #BAD6C2 !important;
	text-decoration: underline !important;
}
.vbmenu_popup
{
	background: #0A1C0B !important;
	color: #BAD6C2 !important;
	border: 1px solid #223B21 !important;
}
.vbmenu_option
{
	background: #21301F !important;
	color: #BAD6C2 !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
}
.vbmenu_option a:link, .vbmenu_option_alink
{
	color: #BAD6C2 !important;
	text-decoration: none !important;
}
.vbmenu_option a:visited, .vbmenu_option_avisited
{
	color: #BAD6C2 !important;
	text-decoration: none !important;
}
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover
{
	color: #BAD6C2 !important;
	text-decoration: underline !important;
}
.vbmenu_hilite
{
	background: #BAD6C2 !important;
	color: #21301F !important;
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink
{
	color: #21301F !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited
{
	color: #21301F !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover
{
	color: #21301F !important;
	text-decoration: none !important;
}
/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 10pt !important; }

/* ***** small padding on 'thead' elements ***** */
td.thead, div.thead { padding: 5px !important; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none !important; }
.pagenav td { padding: 2px 4px 2px 4px !important; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 4px !important; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px !important; }

.huvudBild
{
	border: solid #223B21 !important;
	border-width: 0px 0px 0px 0px !important;
	background: #000000 !important;
}

/* ***** Custom user display text ***** */
.adminuser {
	color: #FFA34F !important;
	font-weight: bold !important;
}

.supermoduser {
color: #FFFFFF !important;
	font-weight: bold !important;
}

.moduser {
	color: #006600 !important;
	font-weight: bold !important;
}

.banneduser {
	color: #000000 !important;
}

/* ***** don't change the following ***** */
form { display: inline !important; }
label { cursor: default !important; }
.normal { font-weight: normal !important; }
.inlineimg { vertical-align: middle !important; }

}
