body,.page { background-color: rgb(0,0,0)!important; }

div[style^='border-bottom'] { border-color: rgb(0,0,0)!important; }
div[style^='background'] { background-color: rgb(0,0,0)!important; }

.tcat, .vbmenu_control { background:rgb(20,20,20)!important; }
.panel, .vBulletin_editor { background: rgb(20,20,20)!important; } 
.thead, .tfoot { background-color: rgb(35,35,35)!important; }
.panelsurround, .vbmenu_option { background: rgb(35,35,35)!important; }
.alt1, .alt1Active { background-color: rgb(50,50,50)!important; }
.alt2, .alt2Active { background-color: rgb(50,50,50)!important; }
