@-moz-document url-prefix("https://cytu.be/r/") {
/* ----- Smoothing default CyTube CSS (works without JS) ----- */


 /*MODS*/
 #motdrow { display: none; }
#footer{
    display:none!important
}
/*Adds Padding to the Top*/

#mainpage{
    padding-top:13.5vh!important
}
/*Chat Emote Size*/
body .channel-emote{
    max-width:156px!important;
    max-height:42px!important
}
body .smoll .channel-emote{
    max-width:15px!important;
    max-height:10px!important
}

/*Emote Menue*/
.emote-preview-container{
    width:62px!important;
    height:62px!important;
    float:left!important;
    text-align:center!important;
    white-space:nowrap!important;
    margin:0
}
.emote-preview{
    max-width:36px!important;
    max-height:36px!important;
    cursor:pointer
}


.queue_temp{
    background-image:none!important
}
.queue_active{
    background-image:linear-gradient(rgba(16,17,18,0.79),#000,#020202)!important;
    background-repeat:no-repeat!important
}
.queue_entry{
    border-width:1px 0!important;
    border-color:#000!important
}
.queue_entry{
    opacity:.775!important
}
ul#queue{
    list-style:decimal!important;
    padding-left:30px!important;
    background-color:rgba(20,22,26,0.7)!important;
    border-left:1px solid #000!important;
    border-right:1px solid #000!important;
    border-top:1px solid #000!important
}
#queue,div#plmeta{
    border-width:0!important
}



/*main*/
	/* -- body -- */

body {overflow-x:hidden;
background-color: #141414;
}



	/* -- buttons -- */

.btn {text-shadow:none;
background-color: #201d2c ;
}

	/* -- modal windows -- */

@media screen and (min-width: 768px) {
	.modal-dialog {max-width:950px !important; margin-top:10px;
	border-radius:0.5px;
	border-color:#251f34;}
}
#videowrap-header {
	border-radius:1px;
	border-color:#251f34;}


	#mainpage .container-fluid {
		width: 100%;
		padding: 0 0 0 0;
	}
	
.col-md-9 {
		width: 76%;
	}



	/* -- brand -- */

.navbar-brand {cursor:default;
background-color: #4b367c ;}
    
	/* -- Header -- */
    
#nav-collapsible {
	background-color: #4b367c !important ;
}

	/* -- MOTD -- */

 #motdwrap {padding:10px; border-color:#251f34; box-shadow:none; background-color: #201d2c; border-radius:1px;
    width:100%;
	margin: 0;
	overflow: hidden;
	} 


#motdrow .col-lg-12{
	width: 100%;
	padding: 0;
}

	
#togglemotd {margin-bottom:-5px}

	/* -- superadmin icon -- */

.glyphicon-globe {margin-right:3px}


    /* -- playlist active box  -- */
	
.queue_entry.pluid-0.queue_temp.queue_active {
	background-image: none;
		 opacity: 1;
     background-color: #2e2c3b !important;
	 border-color:#251f34;
}

    /* -- playlist queued box  -- */
 
.queue_entry.pluid-1.queue_temp {
	background-image: none;
		 opacity: 1;
     background-color: #201d2c !important;
	 border-color:#251f34;
}


.modal-header {
	background-color: #4b367c !important ;
}


    /* -- CHAT -- */
	 #chatheader {
	border-radius:1px;
	border-color:#251f34;}
	 #messagebuffer {
    background-color: #1e1e1e;
    border-radius:1px;
	border-color:#251f34;
	color:#bfbfbf;
	font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
	font-size: 10pt ;
	}
	#chatwrap.col-md-3 {
		
		position:relative;
		padding-left:1px;
		padding-right:1px;
		width:22%;
		min-height:100%;
		height:100%;
	}
	
	 #userlist {
		 
	border-radius:1px !important;
	border-color:#251f34  !important;}
	 

	 #chatline {
    border-radius:1px  !important;
	border-color:#251f34  !important;
	background-color:#1e1e1e;
	color:#1e1e1e;
	 }
	  /* -- Blue users -- */
	 .userlist_item{
		 color:#0073b5;
	 }
	 .username {
		color:#0073b5; 
	 }
	.userlist_owner {
		 color:#ff1d00;
	 }


	/* -- playlist row panes -- */
	

#rightpane, #leftpane {margin-top:5px !important; margin-bottom:5px !important;}

	/* -- left pane wells -- */

#leftpane .well {margin-bottom:5px;}

	/* -- queue titles -- */

.qe_title {margin-left:4px;

}

	/* -- footer -- */

#footer {padding-bottom:5px; height:auto;
background-color: #201d2c ;
}


