css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
body{ background:#002!important}

#fb_menubar {background:#000!important}
#menubar_container,.UIWashFrame_Header .UIMediaHeader_TitleWrapper  ,.profile .profile_color_bar,.profile .profile_top_wash,.UIIntentionalStory, UIRecentActivity_Stream UIStream_Border,#home_stream .UIComposer, #photoborder, #feedwall_controls_wrapper .show_advanced_controls, .UIButton_Suppressed:active, .UIButton_Suppressed:hover, .UIWell  {background:none!important; border:none!important}

#presence #presence_bar_right .hover{background:none!important;color:#33F!important}

#presence_ui 
{background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/gb.png)20px 0px!important; color:#AAA!important}
#presence .presence_bar_button,#presence #buddy_list_tab #buddy_count strong 
{color:#999!important;border:none!important}

.NewHighLight .UIComposer_InputShadow,
.UIComposer .UIComposer_TextArea,.commentable_item .comment_box .comments_add_box textarea,.UIComposer_InputShadow, .UIComposer_STATE_INPUT_FOCUSED .Mentions_Input, .UIComposer_STATE_INPUT_FOCUSED, .Mentions_Input, #buddy_list .buddy_list_typeahead #buddy_list_typeahead_input,
#chat_tab_bar .chat_window .chat_input,.flyout_menu .menu_content .add_tab_app_typeahead .inputtext  
{ background:#005 url(http://i64.photobucket.com/albums/h180/ragingshinigami/textinbg.jpg)!important; color:#000!important;font-weight:bold!important; font-family:comic sans ms!important;}

#chat_tab_bar{background:url(http://i64.photobucket.com/albums/h180/ragingshinigami/CB.png)}

#universal_search #universal_search_input #q, .flyout_menu .flyout_menu_header .flyout_menu_title,.flyout_menu .menu_content, .GBSearchBox_Input, .GBSearchBox_Input input  {background:#000!important; color:#888!important}

#universal_search #universal_search_submit label span,.GBSearchBox_Button  
{ background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/search-1.png)!important }

#presence #buddy_list_tab.focused,#chat_tab_bar .tab_handle.focused, #presence #presence_ui .focused
{background:transparent url(http://i64.photobucket.com/albums/h180/ragingshinigami/CB.png)!important;color:#666!important} 



#presence_applications .app_icon:hover,#presence_applications_icon_garden .icon_garden_elem a.UITooltip:hover,#presence #presence_applications_tab:hover,#presence #buddy_list_tab:hover,#presence #presence_bar_right .presence_section:hover, #presence_applications_icon_garden .icon_garden_elem a.UITooltip:hover, #presence_applications_icon_garden div.icon_garden_elem:hover,  #presence #presence_bar_left .hover
{background:url(http://i64.photobucket.com/albums/h180/ragingshinigami/B.png)!important}

.commentable_item .ufi_section 
{background:url(http://i64.photobucket.com/albums/h180/ragingshinigami/brgradient.gif )0px 25px!important}


#home_stream, #home_sidebar, .UIContentBox, #feedwall_with_composer  
{background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/gradual-dark-1-1.png)fixed!important}

.profile .top_bar ul.tabs li a.tab_link {background: #333!important}
.profile .top_bar ul.tabs li a.tab_link:hover {background:#050505!important}
.profile .top_bar ul.tabs li.selected a.tab_link {background:#001!important;color:#44F!important}

.profile .box .box_header {background:#030303!important;color:#119!important}

#presence .presence_menu_opts , .buddy_list a.friend {background-color:#000!important}
.buddy_list .friend_list, .buddy_list .friendlist_name, .buddy_list span.title a,.buddy_list .switch  {background-color:#001!important}
#presence_bar .presence_menu_opts h2 span, #chat_tab_bar .chat_header {background:url(http://i64.photobucket.com/albums/h180/ragingshinigami/txtbg.png)!important}

#chat_tab_bar .tab_handle.disabled {background: url(http://i64.photobucket.com/albums/h180/ragingshinigami/lighter-trans.png)!important; border:none!important}

#buddy_list_panel .lists a.panel_item span, #buddy_list_panel .settings a.panel_item span , #buddy_list_panel, #chat_tab_bar .chat_window .chat_conv .chat_conv_content, #chat_tab_bar .chat_actions,#chat_tab_bar .chat_info,#presence_bar .application_menu_header 
{background:#000!important; border:none!important;color:#77f!important}
#chat_tab_bar .chat_window .chat_conv p {color:#779!important}

.UIImageBlock, .UIRecentActivity_Content, .UIRecentActivityStory
{background:transparent!important;border:none!important}

.selected .UIFilterList_ItemLink, 
.selected .UIFilterList_ItemLink:hover {background: transparent url(http://i64.photobucket.com/albums/h180/ragingshinigami/back-g-gradient.png)0px -15px!important; border:none!important}
.UIFilterList_Item .UIFilterList_ItemLink:hover {background:none!important; border:inset!important}

.hovered .fb_menu_link, .fb_menu .fb_menu_link:hover {background:#002!important}
.fb_menu_dropdown .fb_menu_item .fb_menu_item_link, .fb_menu_dropdown {background:#005!important;border:none!important}

.fb_menu_dropdown .fb_menu_item .fb_menu_item_link:hover{background:#009!important;color:#000!important;font-weight:bold!important}

.profile .basic_info_summary dl.info dd {color:#333!important;font-weight:bold!important}

#edit_profilepicture {background:#000!important}
#edit_profilepicture_icon {background-color:transparent!important}
.link_menu .menu_content a, .link_menu_list .menu_content ul li a.tab_link {background:#111!important}
.UINestedFilterList_Item_Link:hover, .UINestedFilterList_SubItem_Link:hover, .UINestedFilterList_SubSelected .UINestedFilterList_Item_Link:hover, .UINestedFilterList_CollapseLink:hover {background-color:#000!important}
.UINestedFilterList_Selected .UINestedFilterList_Item_Link, .UINestedFilterList_SubItem_Selected .UINestedFilterList_SubItem_Link {background-color:#004!important}


}
