css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {

@media (min-width: 876px){
    ytd-browse[page-subtype="history"] ytd-two-column-browse-results-renderer{
        width: calc(100% - 20px) !important;
        margin-left: 20px !important;
        margin-right: 0 !important;
    }

    ytd-browse[page-subtype="history"] #primary{
        padding-right: 280px !important;
    }
    ytd-browse[page-subtype="history"] #primary ytd-section-list-renderer{
        margin-right: 20px !important;
    }

    ytd-browse[page-subtype="history"] #secondary ytd-browse-feed-actions-renderer {
        width: 240px !important;
        padding: 0 20px 0 20px !important;
    }
    ytd-browse[page-subtype="history"] #secondary tp-yt-paper-input.ytd-search-box-renderer {
        width: 240px !important;
    }
    ytd-browse[page-subtype="history"] #secondary ytd-search-box-renderer {
        margin: 4px 0px 8px !important;
    }
}
/*[[bug]]*/
/*[[zoom]]*/
}

style_setting_options.value:
@media (min-width: 876px){
    /* thumbnail preview kinshi */
    ytd-browse[page-subtype="history"] div#mouseover-overlay.ytd-thumbnail{
        display:none !important;
    }
    
    /* atodemiru, queue ni tsuika button hihyouji */
    ytd-browse[page-subtype="history"] ytd-thumbnail-overlay-toggle-button-renderer{
    	display: none !important;
    }
    
    /* hover de kakudai  normal video */
    ytd-browse[page-subtype="history"] ytd-video-renderer ytd-thumbnail:hover{
        transform: scale(2.6) !important;
        transform-origin: left center !important;
        transition: transform .4s cubic-bezier(.5,1,.89,1) 1s;
        z-index: 1000 !important;
    }
    ytd-browse[page-subtype="history"] ytd-thumbnail:hover ytd-thumbnail-overlay-resume-playback-renderer{
        display:none !important;
    }
    ytd-browse[page-subtype="history"] ytd-thumbnail:hover ytd-thumbnail-overlay-time-status-renderer{
        display:none !important;
    }

    /* hover de kakudai short video */
    ytd-browse[page-subtype="history"] ytd-reel-shelf-renderer ytd-thumbnail:hover{
        transform: scale(2) !important;
        transform-origin: center 30% !important;
        transition: transform .4s cubic-bezier(.5,1,.89,1) 1s;
        z-index: 1000 !important;
    }
}
