css:
@-moz-document domain('lpsg.com'), url-prefix('https://www.lpsg.com') {
 
.p-staffBar {
z-index: 150;
font-size: 1.6rem;
color: rgba(255,255,255,0.7);
background: #958237;
border-radius: 0px;
}

.block--messages.block .message, .js-quickReply.block .message, .block--messages .block-row, .js-quickReply .block-row {
    color: #fff;
}

.label.label--red {
    color: #fff;
    background: #958237;
    border-color: #534722;
}

a {
    color: #CEB941;
text-decoration: none;
}
.userBanner {
        color: #323232;
}

.p-nav-list .p-navEl.is-selected {
    color:  #958237;
    background: #323232;
    border-top: 1px solid #4c4c4c;
}

.button.button--cta, button.button a.button.button--cta {
    color: #fff;
    background: #958237;
    padding: 0 10px;
    border: none;
        border-top-color: currentcolor;
        border-right-color: currentcolor;
        border-bottom-color: currentcolor;
        border-left-color: currentcolor;
    box-shadow: none;
    border-radius: 4px;
    height: 40px;
    line-height: 40px;
    border-color: #57aae1;
}

.menu-tabHeader .tabs-tab.is-active {
    color: #fff;
    border-color: #958237;
}

.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 1.4rem;
    color: #fff;
    background: #2d2d2d;
    border: 0 solid #4c4c4c;
        border-top-color: rgb(76, 76, 76);
        border-top-style: solid;
        border-top-width: 0px;
    border-radius: 0;
    border-top: 2px solid #958237;
}

.button.button--primary, button.button a.button.button--primary {
    color: #fff;
    background: #958237;
    padding: 0 10px;
    border: none;
        border-top-color: currentcolor;
        border-right-color: currentcolor;
        border-bottom-color: currentcolor;
        border-left-color: currentcolor;
    border-color: #57aae1;
}

.message .reactionsBar {
    margin-top: 8px;
    background: #363636;
    border: 1px solid #222;
    border-radius: 4px;
    padding: 8px;
    margin-top: 0;
    margin-bottom: 8px;
}

.pollResult-bar > i {
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
    background: #958237;
    border-radius: 4px;
}


.bbCodeBlock {
    display: flow-root;
    margin: .5em 0;
        margin-top: 0.5em;
    background: #323232;
    border: 1px solid #4c4c4c;
        border-left-color: rgb(76, 76, 76);
        border-left-style: solid;
        border-left-width: 1px;
    border-left: 2px solid #958237;
}

.bbCodeBlock-title {
    padding: 8px 15px;
    font-size: 1.4rem;
    color: #958237;
    background: #2d2d2d;
}

.userBanner.verified {
    background-color: #CBCBCB;
    border-color: #6C6C6C;
        border-radius: 5px;
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
    	border-bottom-left-radius: 4px;
        border-bottom-right-radius: 4px;
    
}
.block--messages.block .message, .js-quickReply.block .message, .block--messages .block-row, .js-quickReply .block-row {
    color: #DEDAD1;
}
.pairs > dt {
    padding: 0;
    margin: 0;
        margin-right: 0px;
    color: #666157;
}

.userBanner.userBanner--staff, .userBanner.userBanner--primary {
    color: #BE7A53;
    background: #BE7A53;
    border-color: #5a5a5a;
    border-color: #6ba65e;
    color: #fff;
    background: #BE7A53;
    border-color: #6D4329;
    border-radius: 4px;
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
    border-color: #6D4329;
}

.userBanner.moderator {
    background-color: #DEC3B4;
    border-color: #B88A76;
    border-radius: 5px;
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
    	border-bottom-left-radius: 4px;
        border-bottom-right-radius: 4px;
    
}

.structItem.is-unread {
    background: rgba(100,100,100,0.1);
}

.userBanner.userBanner--staff, .userBanner.userBanner--primary {
    color: #323232;
        border-radius: 5px;
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
    	border-bottom-left-radius: 4px;
        border-bottom-right-radius: 4px;
    
}
.userBanner.gold {

    background-color: #e8d68e;
    border-color: #e6c955;
        border-radius: 5px;
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
    	border-bottom-left-radius: 4px;
        border-bottom-right-radius: 4px;
    
}

.userBanner.userBanner--skyBlue {
        color: #323232;
}

.avatar {
    border-radius: 2px;
    vertical-align: top;
    overflow: hidden;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    line-height: 1;
    border-style: solid;
    border-width: 1px;
    border-color: rgba(0, 0, 0, 0.35);
    border-radius: 5%;


}

.reaction--1 .reaction-text {
    color: #CEB941;
}

.reaction--1 .reaction-sprite {
    width: 32px;
    height: 32px;
    background: url('https://i.imgur.com/HkLNp2b.png') no-repeat 0px 0px;
        background-size: auto;
    background-size: 100%;
}

.memberHeader-blurbContainer {
    color: white;
    background: rgba(149,130,55,0.4);
    border-radius: 4px;
    text-shadow: 0 0 1px #000;
    margin-top: 8px;
}
    
 .avatar.avatar--m {
    width: 96px;
    height: 96px;
    font-size: 58px;
}




.message-cell--user .message-userBanner.userBanner .userBanner-before, .message-cell--user .message-userBanner.userBanner .userBanner-after {
    content: '';
    width: 5px;
    height: 4px;
    background: transparent;
    position: absolute;
    top: -4px;
}
   
body .p-pageWrapper .uix_searchBar .uix_searchBarInner .uix_searchForm i {
    text-align: center;
    font-size: 24px;
    background: none;
    color: #777777;
}
.blockLink.is-selected {
color: #AC9828;
font-weight: 600;
background: #353535;
border-left: 2px solid #AC9828;
padding-left: 13px;
}

.block-tabHeader .tabs-tab.is-active {
    background: none;
    color: #888888;
    background: #2d2d2d;
    border-bottom-color: #2d2d2d;
}

.p-breadcrumbs > li:last-child a {
color: #CECECE;
}
}
