css:
@-moz-document url("https://www.google.com/")
{

/*background color*/
  body, input, button
  {
    background-color: #1F2125;
  }


/*searchbar when not active*/
  .RNNXgb
  {
    background: #fff0;
    border: 1px solid  #E7407E;
  }

  /*searchbar hovere*/
  .RNNXgb:hover
  {
    background-color: #565656;
  }

/* search buttons color*/
  .lJ9FBc input[type="submit"], .gbqfba
  {
    background-color: #2F3231;
    border: 1px solid #2F3231;
    border-radius: 4px;
    color: #afb5b9;
  }

  /*search menu color */
  .aajZCb
  {
    background: #424244;
  }
  
  /*google logo opacity 0 */
  .lnXdpd
  {
    opacity: 0;
  }

/*replacement logo */
  .k1zIA
  {
    background-image: url(https://res.cloudinary.com/userstyles/image/upload/v1634044131/SG-Fixed_fxia8t.svg) !important;
    background-repeat: no-repeat;
    background-size: 85%;
    background-position: 50% 100%;
  }

  /*hight of logo */
  .rSk4se
  {
    max-height: 150px !important;
  }


/* search links color */
  .WggQGd
  {
    color: #E7407E !important;
  }

  /* search text color */
  .wM6W7d
  {
    color: #eee;
  }

  /* language text color*/
  a
  {
    color: #898989;
  }

  /* "google offered in" text */
  #SIvCob
  {
    color: #5e6062;
  }
 
  /* gmail images to right color */
  a.gb_f, span.gb_f
  {
    color: #898989;
  }
}
