css:
@-moz-document url-prefix("http://aishiteru.pl/") {
@font-face {
    font-family: 'Lato';
    src: url(https://fonts.gstatic.com/s/lato/v20/S6uyw4BMUTPHjx4wXiWtFCc.woff2) format('woff2');
}
body, .site-footer .site-tagline, .form-control, input[type="text"], input[type="url"], input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], select, textarea, .work-post.format-link .entry-header a:after {
    color: #94c3c3 !important;
}
* {
    font-family: 'Lato' !important;
}
body.custom-background {
    background: none !important;
}
.wp-block-media-text.alignwide.has-media-on-the-right.is-stacked-on-mobile.is-vertically-aligned-center {
    grid-template-columns: auto 40% !important;
}
.main-container {
    padding-top: 1em !important;
}
.wp-block-media-text__content, .entry-content {
    color: #666666 !important;
}
#menu-item-134 a:not(:hover):not(:focus) {
    color: #088787 !important;
}
.sf-menu>li>a:hover, .sf-menu>li>a:focus, .sf-menu>li.current_page_item>a, .sf-menu>li.current-menu-item>a:not(#menu-item-134) {
    color: #000000 !important;
}
.header-logo {
    width: 75% !important;
    max-width: 100px !important;
    margin-top: 2.75vh !important;
}
.top-header {
    margin-top: -3% !important;
}
.menu-icon {
    margin-top: 3.5vh !important;
}
}
