@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.warez-bb.org) {


/* This style will be applied to outermost area of the forum */ 
body:not(.gaybody){
  background:#000000 !important;
  font-size:12px; 
  font-family:Verdana,Arial,Helvetica,sans-serif;
  margin:6px;
  padding:0;
}

/* This style will be applied for gradient background at the top of each page where logo is displayed. Its a forum header. */
.topbkg{
  background: #BABABA url(http://i38.tinypic.com/9sus1i.jpg) repeat-x !important;
}

/* This style is applied to the logo of the forum. */
table.topbkg td a[href*="index.php"] > img{
	height: 110px !important;
	width: 0 !important;
	padding-right: 290px !important;
	background: url(http://i35.tinypic.com/1fvdd2.jpg) no-repeat right;
}

/* The text used at the header of the page which is the main title of the forum. Its "Come as guests, stay as family." for Warez-BB */
.header_maintitle,h1{
  font:bold 20px/120% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif;
  text-decoration:none; 
  color:#0947ae !important;
}

/* The style of subtext for main title of the forum. Warez-BB normally displays some important messages and some quotes in the area */
.header_genmed{
  color:#0947ae !important;
}

/* The top navigation bar below the forum header which shows menus like Search, Profile, Logout etc */
.topnav{
  font-size:10px;
  background: #E3EBEF url(http://i38.tinypic.com/oruvr8.jpg) repeat-x !important;
  height:21px;
  white-space:nowrap;
  border: 0px solid #71b9de !important;
  border-width: 1px 0px 1px 0px;
}

/* Style applies to Second outermost area of forum between actual forum area box and outermost area */
.bodyline:not(.gaybodyline){
  background: #333333 repeat-x !important;
  background-repeat:repeat;
  border:1px solid #313131 !important;
}

/* The red rounded rectangle which shows where show you new PM notification. Thanks to glenny for reporting and its fixed now. (: */
table.bodyline > tbody > tr > td > table > tbody > tr > td > div > div > p:not(.gtfodarktheme){
  color: #0947ae !important; 
}

/* Advertisement area rounded rectangle at top of the page and powered by phpBB rounded rectangle at bottom of the page */
table.bodyline > tbody > tr > td > table > tbody > tr > td > div:not(.gtfodarktheme){
	background: #111111 !important;
	border: 1px solid #000000 !important; -moz-border-radius: 8px;
}

/* This is the gradient background at the top of the categories which shows text like Forum, Topics, Posts, Last Post */
th{
  background: #031637 !important;
}

/* The text style applied for area which shows text like Forums, Topics, Post, Last Post */
table.forumline > tbody > tr > th{
  color:#FFFFFF !important
}

/* The style applied to Category name in the forum like Listing, Reuests. */
td.cat{
  background: #031637 !important;
}

/* The backgrounds displayed for forum names and description. Also this background will be applied to first post of the topic */
.row1{ background : #000000 repeat-x !important }
.row2, .helpline{ background : #222222 repeat-x !important ;}
.row3 { background : #222222 repeat-x !important ;}

/* This style will be applied for footer advertisement rounded rectangle and Time, Queries stats rectangle */
.footerline{
  background: #222222 repeat-x !important;
  background-repeat:repeat;
  border:1px solid #888 !important;
  -moz-border-radius:6px;
}

/* Category Search box on index page - Style will be applied to Textbox */
input.post[name="search_keywords"]:not([size="30"]):not([style="width: 100px;"]){
  background: #FFFFFF !important; color:#0947ae !important; border: 1px #000 !important
}

/* Category Search box on index page - Style will be applied to "Go" button */
input.button[value="Go"]{
  background: #FFFFFF !important; color:#0947ae !important;
}

/* Links used in the site, user rank colors on main page looks fine with it, thanks DI :) */
a:link:not([style]):not(:hover), a:active, a:visited:not([style]):not(:hover), a.postlink, 
b > a:link:not([style]):not(:hover), b > a:active:not(:hover), b > a:visited:not(:hover), b > a.postlink:not(:hover){
  color:#1f68e8 !important;
  text-decoration:none;
  border: 0px !important;
}

/* Mouseover hover for links used in the site */
a:hover, b > a:hover{
  color:#0947ae !important;
}

/* Description of the forum names */

.genmed{
  color:#FFFFFF !important;
}

/* The largest text used in the index page title and topic title etc. */
.maintitle,h1{
  font:bold 20px/120% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif;
  text-decoration:none;
  color:#fef5b8 !important;
}

/* Style applies to Space row between 2 posts */
td.spacerow{
  background:#444444 !important;
}

/* This style applies to Title of any input like Textbox, checkbox etc like Subject, Message body, Emoticons, Options text */
.explaintitle{
  color:#fef5b8 !important;
}

/* This style applies to profile details in the post like User rank, Joined, Posts and Location text */
.postdetails{
  color:#fef5b8 !important;
}

/* This style is used to display errors such as invalid session while posting the topic */
.errorline{
  background: #BABABA !important;
  color: #fef5b8 !important;
}

/* style applied to cat class which shows text like statistics, Compressed Who is Online, Users viewing this forum etc */
.cat{
  color: #FFFFFF !important;
}

/* Style applied to the minus button which is used to hide the forum category */

img[src*="exp_minus.gif"]{
  display: none;
}

a[href$="1);"]:before{
  content: url(http://i35.tinypic.com/nf0dw9.jpg);
}

/* Style applied to the minus button which is used to show the forum category */
