/* This style will be applied to the glow around the quote and code tag */

.quote, .code
{
  -moz-box-shadow: #093175 1px 1px 1px;
  -moz-box-shadow: #093175 1px 1px 1px, #093175 1px 1px 1px, #093175 1px 1px 11px, #093175 1px 1px 11px;  
}

/* This style will be applied to glow around input text ,textarea and buttons */

textbox:focus, textarea:focus, input:focus
{
  -moz-box-shadow: #093175 1px 1px 1px, #093175 1px 1px 1px, #093175 1px 1px 11px, #093175 1px 1px 11px;  
}


select:focus, input:not([type="checkbox"]):not([type="radio"]):focus
{
