}

.quote{color: #fecc6b !important;
	border-left: 4px solid #444 !important;
	border-right: 1px dotted #444 !important;
	border-top: 1px dotted #444 !important;
	border-bottom: 1px dotted #444 !important;
	padding: 4px;
	background: #444 bottom right no-repeat !important;
	margin: 0px auto 8px auto;}

.code{font-family: Courier, Courier New, Verdana, Arial;
	color: #fecc6b !important;
	background-color: #444 !important;
	border: 1px dotted #000 !important;
	padding: 2px;
	width:98%;
	margin: 0px auto 0px auto;}


