css:
@-moz-document url-prefix("https://bobrtc.tel/") {
.conversationcard {
    width: 99%;
    margin: 0rem 0rem 1rem 0rem;
}
body, html {
    margin: 0;
    font-size: 100%;
    background: #020024;
    font-family: Nunito, sans-serif;
}
.card {
    margin-top: 0.09rem;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #030034;
    background-clip: border-box;
    border: 1px solid #0000;
    border-radius: 0.375rem;
}
.card {
    box-shadow: 2px 2px 2px #ffa500;
    border: none;
}
.conversationcard {
    padding-bottom: 30px;
    background: #030034;
    padding-bottom: 30px;
    border: 0 solid;
    padding: 10px;
    box-shadow: 2px 2px 2px #ffa500;
    margin-bottom: 1.3rem;
    background: #030034;
    -webkit-animation: zoomIn ease-in .4s;
    animation: zoomIn ease-in .4s;
    -moz-animation: zoomIn ease-in .4s;
    -o-animation: zoomIn ease-in .4s;
    -ms-animation: zoomIn ease-in .4s;
}
.navbar-dark {
    background-color: #030034;
}
nav.navbar.navbar-fixed-top.navbar-expand-lg.navbar-dark.navShadow {
    box-shadow: 0px 10px 20px #ffab00!important;
}
.navbar, .navbar-fixed-top, .navbar-expand-lg, .navbar-dark {
    box-shadow: 0px 10px 20px #ffab00!important;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px #ffa500;
    border-radius: 10px;
}
::-webkit-scrollbar {
    width: 6px;
}
.footer_w3layouts_section_1its {
    background: #020020;
}
.cpy-right {
    background: #020024;
}
::-webkit-scrollbar-thumb {
    border-radius: 16px;
    -webkit-box-shadow: inset 0 0 22px #ffa500;
}
.card-body {
    -webkit-box-flex: 1;
    -ms-flex: 1 1 auto;
    flex: auto;
    padding: 1.5rem;
    color: #afafaf;
    background-color: #030034;
}
.navbar-expand-lg .navbar-collapse .dropdown-item {
    font-size: 1.12rem;
    font-weight: 600;
    background-color: #030034;
    color: orange;
    margin-left: -5px;
    -webkit-animation: scale-up-right .4s cubic-bezier(.39, .575, .565, 1)both;
    animation: scale-up-right .4s cubic-bezier(.39, .575, .565, 1)both;
    text-align: center;
}
.navbar-expand-lg .navbar-collapse .dropdown-item:hover {
    background: orange;
    color: #030034;
    -webkit-animation: scale-up-right .4s cubic-bezier(.39, .575, .565, 1)both;
    animation: scale-up-right .4s cubic-bezier(.39, .575, .565, 1)both;
}
.alert-warning {
    color: #ffb100;
    background-color: #030034;
    border-color: #ffb100;
    text-align: center;
}
.btn-primary:focus {
    box-shadow: 0 0 0 0px orange;
}
.show>.btn-primary.dropdown-toggle:focus {
    box-shadow: 0 0 0 0px #ffa500;
}
.alert-danger {
    color: #934236;
    background-color: #030034;
    border-color: #ffd0c5;
}
.alert-danger {
    color: #ff2100;
    background-color: #030034;
    border-color: #ff3000;
}
.bg-dark-dark {
    background-color: #030034!important;
}
.badge-dark {
    color: orange;
    background-color: #030034;
}
.alert-success {
    text-align: center;
    color: orange;
    background-color: #030034;
    border-color: #0000;
    box-shadow: 2px 2px 2px orange;
    animation: fadein 3s;
    -moz-animation: fadein 1.5s;
    -webkit-animation: fadein 1.5s;
    -o-animation: fadein 1.5s;
    width: 75%;
}
.alert.alert-success {
    margin: 0px 233px!important;
}
.badge-pill {
    padding-right: 0.875em;
    padding-left: 0.875em;
    border-radius: 50rem;
    background-color: orange!important;
    color: #030034;
    font-weight: bolder;
}
.forumhandle {
    margin-left: 3px;
    font-weight: bolder;
    font-size: 1.7vh;
    color: orange;
}
#addbutton {
    margin-top: -10px;
    width: auto;
    margin-left: 33px;
    background-color: #030034;
    border-color: orange;
}
#addbutton:hover {
    background: orange;
    border-color: #030034;
    color: #030034;
}
.custom-control-input:checked~.custom-control-label:before {
    color: #fff;
    border-color: orange;
    background-color: orange;
    -webkit-box-shadow: none;
    box-shadow: none;
}
.list-group-item {
    position: relative;
    display: block;
    padding: 1rem 1.5rem;
    margin-bottom: -1px;
    background-color: #030034;
    border: 1px solid #0000;
    font-weight: 600;
}
p {
    color: orange!important;
    margin: 0;
    line-height: 1.9em;
    letter-spacing: .5px;
    font-size: .9rem;
    color: #afafaf;
    padding: 5px;
}
.forum a {
    color: #fff!important;
    font-size: 1rem!important;
}
.forum a:hover {
    font-size: 1rem!important;
    text-shadow: 0 0 12px #e2e2e2!important;
    -moz-transition: all .2s ease-in;
    -o-transition: all .2s ease-in;
    -webkit-transition: all .2s ease-in;
    transition: all .2s ease-in;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    margin-bottom: 0.5rem;
    font-family: inherit;
    font-weight: 500;
    line-height: 1.3;
    color: #ffa500;
}
.custom-select {
    color: #ffa500;
    background-color: #020024;
    border-color: #ffa500;
}
.actions-search {
    display: none;
    opacity: 0;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: #030034;
    border-color: #ffa500;
    z-index: 1;
    border-radius: 0;
}
.input-group-merge .input-group-text {
    color: #ffa500;
    background-color: #fff;
    border: 1px solid #e0e6ed;
}
.input-group-merge .form-control:not(:last-child):not(.is-valid):not(.is-invalid) {
    border-right: 0;
    padding-right: 0;
    background: #030034;
    color: orange;
}
.footer a, .footer p {
    color: #ffab009e;
    margin: 8px 0;
}
.cpy-right a, .cpy-right p {
    letter-spacing: 1px;
    color: #ffab009e;
}
.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 1.5rem + 2px);
    padding: 0.75rem 1.25rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #020024;
    background-color: #fff;
    background-clip: padding-box;
    border: 1px solid orange;
    border-radius: 0.25rem;
    -webkit-box-shadow: inset 0 1px 1px #1f2d3d;
    box-shadow: inset 0 1px 1px #1f2d3d;
    -webkit-transition: all .2s ease;
    transition: all .2s ease;
}
.bg-dark {
    background-color: #020024!important;
}
b, strong {
    font-weight: 700;
    color: orange;
}
.dropdown-item, .dropdown-menu {
    color: #fff;
    background-color: #030034;
    color: orange;
}
.alert-dark {
    font-size: 1.1rem;
    text-align: center;
    background-color: #020024;
    border-color: #0000;
    box-shadow: 2px 2px 2px #e2e2e2;
    animation: fadein 3s;
    -moz-animation: fadein 1.5s;
    -webkit-animation: fadein 1.5s;
    -o-animation: fadein 1.5s;
}
.btn-dark {
    color: #fff;
    background-color: #020024;
    border-color: #ffa500;
    border-radius: 0.375rem;
    box-shadow: 2px 2px 2px #ffa500;
    margin: 0.375rem;
}
#loginButton.btn-primary {
    color: #ffa500;
    background-color: #030034;
    border-color: #0000;
    padding: 5px 20px;
    margin-left: -20px;
}
#loginButton.btn-primary:hover {
    color: #030034;
    background-color: #ffa500;
    border-color: #030034;
    padding: 5px 20px;
    margin-left: -20px;
}
.modal-content {
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    width: 100%;
    pointer-events: auto;
    background-color: #030034;
    background-clip: padding-box;
    border: 1px solid rgba(31,45,61,.2);
    outline: 0;
}
.table-dark td, .table-dark th, .table-dark thead th {
    border-color: #ffa500;
}
.table-dark {
    color: #ffa500;
    background-color: #030034;
}
.pref-card {
    border: 1px ridge orange!important;
    background-color: #030034!important;
    width: 100%;
    padding: 13px;
}
.form-control {
    display: block;
    width: 100%;
    height: calc(1.5em + 1.5rem + 2px);
    padding: 0.75rem 1.25rem;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: orange;
    background-color: #fff;
    background-clip: padding-box;
    border: 1px solid orange;
    border-radius: 0.25rem;
    -webkit-box-shadow: inset 0 1px 1px #1f2d3d;
    box-shadow: inset 0 1px 1px #1f2d3d;
    -webkit-transition: all .2s ease;
    transition: all .2s ease;
}
section {
    background-color: #020024;
}
h3.heading {
    font-size: 45px;
    font-weight: 600;
    color: orange;
    text-transform: capitalize;
    letter-spacing: 0;
}
ul.social_section_1info a {
    color: orange;
    margin-right: 10px;
    font-size: 13.5px;
    margin-right: 2px;
    width: 35px;
    height: 35px;
    background: #030034;
    padding: 9px 15px;
    text-align: center;
    line-height: 35px;
    letter-spacing: 1px;
}
.modal {
    backdrop-filter: blur(5px);
    background-color: #00000000;
}
.modal-dialog.full-screen .modal-content {
    position: absolute;
    left: 180px;
    right: 20px;
    top: 20px;
    bottom: 20px;
    width: 75%;
    background: #0200246e;
}
.btn-dark:hover {
    color: #020024;
    background-color: orange;
    border-color: orange;
    box-shadow: 2px 2px 2px #020024;
    margin: 0.375rem;
}
.btn-dark:not(:disabled):not(.disabled).active, .btn-dark:not(:disabled):not(.disabled):active, .show>.btn-dark.dropdown-toggle {
    color: orange;
    background-color: #141b24;
    border-color: #020024;
}
.btn-dark:hover {
    color: orange;
    background-color: #020024;
    border-color: orange;
    box-shadow: 2px 2px 2px #020024;
    margin: 0.375rem;
}
.btn-dark:not(:disabled):not(.disabled).active, .btn-dark:not(:disabled):not(.disabled):active, .show>.btn-dark.dropdown-toggle {
    color: orange;
    background-color: #020024;
    border-color: #020024;
}
}
