css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(profiles.delphiforums.com) {

/* Brand Strip */
#header {
	background-color: #1e2125 !important;
	background-image: url(http://www.angehowe.com/delphi/skins/blueblackbrand.png) !important;
	background-repeat: repeat-x !important;
	background-position: 0% 0% !important;
	font-size: .8em !important;
	color: #f7c7b7 !important;
	border-bottom: 20px solid #0f0 !important;
}

#header #logo a img {
opacity: 0;
}

#header #logo  {
background-image: url(http://www.angehowe.com/delphi/DFLogo-sm.png) !important;
background-position: 50% 50% !important;
background-repeat: no-repeat;
width: 99px !important; 
height: 44px !important
}
 
#header a {
	color: #cbe0ed  !important;
	text-decoration: none;
}

#header #user {
  border-left: 1px solid #2a2f38 !important;
}

#header #user a.memberName {
	color: #ed8968 !important;
}

#header #messages {
border-color: #2a2f38 !important;
}

#header #messages a {
font-size: .9em !important;
color: #64a4ea !important;
}
#header #messages .newMessages {
	color: #ed7d58 !important;
  font-weight: bold;
}
#header #messages .noMessages {
	color: #cbe0ed !important;
}

#header #messages span.toMe {
  display: none;
}

#header #buttons {
	font-size: .8em !important;
	opacity: .5;
}
#header #buttons a {
	color: #c2c8d3 !important;
}
#header #buttons a#help {
	background-color: transparent !important ;
	background-image: url(http://www.angehowe.com/delphi/Help-gray.png) !important ;
letter-spacing: -1px;
}
#header #buttons a#help:hover {
	background-color: transparent !important ;
	background-image: url(http://www.angehowe.com/delphi/Help-LtBlue.png) !important ;
}

#header #buttons a#chat {
	background-color: transparent !important ;
	background-image: url(http://www.angehowe.com/delphi/Chat-gray.png) !important ;
letter-spacing: -1px;
}

#header #buttons a#chat:hover {
	background-color: transparent !important ;
	background-image: url(http://www.angehowe.com/delphi/Chat-LtBlue.png) !important ;
}

#header #buttons a#allForums {
	background-color: transparent !important ;
	background-image: url(http://www.angehowe.com/delphi/Forums-gray.png) !important ;
	background-position: 0 3px !important;
	letter-spacing: -1px;
}

#header #buttons a#allForums:hover {
	background-color: transparent !important ;
	background-image: url(http://www.angehowe.com/delphi/Forums-LtBlue.png) !important ;
	background-position: 0 3px !important;
	}

#header #buttons a#myForums {
	background-color: transparent !important ;
	background-image: url(http://www.angehowe.com/delphi/MyForums-gray.png) !important ;
	background-position: 0 3px !important;
letter-spacing: -1px;
}

#header #buttons a#myForums:hover {
	background-color: transparent !important ;
	background-image: url(http://www.angehowe.com/delphi/MyForums-LtBlue.png) !important ;
	background-position: 0 3px !important;
	}

/* MyForums Page */
.os-brandtop {
	background-color: #5e6985 !important;
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack.gif);
	background-repeat: repeat-x !important;
	background-position: 100% 100% !important;
	color: #000000 !important ;
}
 .os-brandtop .os-btn span,
 .os-brandtop div,
  .os-brandtop div a{
 color: #000000 !important;
 }
 
.os-wrapper .os-main {
 background-color: #171717 !important; 
 color: #fff !important;
 border: 1px solid #1f2329 !important;
 }
 
/* main bg for forum sidebar sections */
.os-container {
	background-color: #171717 !important;
	color: white !important;
}

div.os-container p, 
div.os-container th, 
div.os-container td, 
div.os-container input, 
div.os-container textarea {
	color: #c0c0c0 !important;
	font-size: 1em !important ;
	border: 0px solid #161719 !important;
}

/* main bg for forum sections */
.os-maincontentpanel,  .os-contenttable {
	background-color: #000 !important;
	border-color: #424959 !important;
}


/* Adds padding on right side of forum promos/ad space */
.os-sidebarleft {
	margin-right: 14px !important ;
}

/* Membername bar bg */
div.os-appheader div.os-bar {
	background-color: #000000 !important;
	background-image: url(http://www.angehowe.com/delphi/skins/blackbar3.png) !important ;
	background-position: 100% 0%;
	background-repeat: no-repeat;
}

/* removes gray bg image */
div.os-appheader div.os-bar div.os-left,
div.os-appheader div.os-bar div.os-right {
	background-image: none !important ;
}

/* header text for forum sections */
tr.os-head td, div.os-profilemain div.os-panel div.os-head {
	background-color: #4e676a !important ;
	background-image: url(http://www.angehowe.com/delphi/skins/v_ribbon2_orange.png);
}

div.os-profilemain div.os-panel {
	border-color: #3b0000 !important;
}

div.os-profilemain div.os-panel div.os-head h2 .os-memberlink {
color: #fff !important;
}


/* header text for forum sections */
tr.os-head h3 {
	color: #ffffff !important ;
}

/* column header for forums section */
tr.os-head th {
	background-color: #000000 !important;
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack.gif);
	color: #ffffff !important ;
}

/* Forums List, odd rows */
tr.os-value td,
tr.os-valuealt td,
/* MyForums List All table cells */
div.os-myforums td {
	background-color: #000000 !important;
	color: #fff !important ;
}

/* top tabs - bg only, all of them! */
div.os-tabs ul li.os-tab {
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack_top.png);
	background-color: #111214 !important;
	background-repeat: repeat-x;
	border: 1px solid #576179 !important;
}

/* top tabs - text/link only, all of them! */
div.os-tabs ul li.os-tab a:link, 
div.os-tabs ul li.os-tab a:visited {
	color: #959595;
}

div.os-tabs ul li.os-tab a:hover {
	background-color: transparent;
	color: #9fc7f2 !important;
	text-decoration: none!important;
}

/* MyProfile/Browse/Search icons on top bar */
div.os-right li a.os-btnmyprofile:link,
div.os-right li a.os-btnmyprofile:visited,
div.os-right li a.os-btnbrowse:link, 
div.os-right li a.os-btnbrowse:visited,
div.os-right li a.os-btnsearch:link, 
div.os-right li a.os-btnsearch:visited {
	color: #000 !important ;
}


/* top tabs, current page bg only */
div.os-tabs ul li.os-current {
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack_bottom.png);
	background-color: #111214 !important;
	background-repeat: repeat-x;
	background-position: 0% 100%;
}

/* top tabs - current tab text/link only */
div.os-tabs ul li.os-current a:link, 
div.os-tabs ul li.os-current a:visited {
	color: #1d6fca;
}




/* My Forums tabbed bar below membername and tabs */
div.os-tabsubhead div.os-bar {
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack_top.png);
	background-color: #111214 !important ;
	background-repeat: repeat-x;
	color: white;
	border: 1px solid #576179 !important;
}

/* My Inbox - Page bar - bg only */
div.os-Paginator  {
	background-color: #4e676a !important ;
	background-image: url(http://www.angehowe.com/delphi/skins/v_ribbon2_orange.png);
}


/* My Inbox - Page bar - text only */
div.os-Paginator div.os-left li a:link,
div.os-Paginator div.os-left li a:visited,
div.os-Paginator div.os-right li a:link,
div.os-Paginator div.os-right li a:visited {
	color: #fff !important ;
}

div.os-Paginator div.os-left li a:hover,
div.os-Paginator div.os-right li a:hover {
	color: #fff !important ;
}

/* Left side text on My Forums tabbed bar */
div.os-right li span.os-Disabled {
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack_bottom.png) !important ;
	background-color: #111214 !important ;
	background-repeat: repeat-x !important ;
	background-position: 0% 100% !important ;
	color: #1d6fca !important ;
}

/* Left side text on My Forums tabbed bar - inactive/disabled tab hovered */
div.os-right li span.os-Disabled a:hover {
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack_bottom.png) !important ;
	background-color: #111214 !important ;
	background-repeat: repeat-x !important ;
	background-position: 0% 100% !important ;
	color: #ea4e00 !important ;
}

/* Left side text on My Forums tabbed bar - active/enabled tabs hovered */
div.os-bar li a:link, 
div.os-bar li a:visited {
	background-color: transparent !important ;
	color: #959595 !important ;
}
/* Left side text on My Forums tabbed bar - active/enabled tabs hovered */
div.os-bar li a:hover {
	background-color: transparent !important ;
	color: #ffffff !important ;
}

/* My Forums tabbed bar - active/enabled tabs hovered */
div.os-ProfileMenu div.os-right li a:hover {
	background-color: #000000 !important;
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack.gif) !important ;
	background-repeat: repeat-x !important ;
	background-position: 50% 50% !important ;
	color: #fff !important ;
}

/* My Forums tabbed bar - active/disabled tab */
div.os-bar div.os-right span.os-Disabled a {
	background-color: #ffa500;
}

/* My Forums page Forums list, Center Section only  - all unvisited links */
/* Adjust the colors as you wish: http://www.w3schools.com/html/html_colornames.asp */
.os-myforums a:link{
	color: #f58e6c !important;
	font-weight: bold !important;
}

/* My Forums page Forums list, Center Section only  - all visited links */
/* Adjust the colors as you wish: http://www.w3schools.com/html/html_colornames.asp */
.os-myforums a:visited {
	color: #64a4ea !important;
	font-weight: bold !important;
}

/* My Forums page Forums list, Center Section only  - all hovered links */
/* Adjust the colors as you wish: http://www.w3schools.com/html/html_colornames.asp */
.os-myforums a:hover {
	background-color: transparent !important;
	color: #ea4e00 !important;
	font-weight: bold !important;
}

/* My Forums page Forums list, Center Section only  - all text except links */
/* Adjust the colors as you wish: http://www.w3schools.com/html/html_colornames.asp */
.os-myforums td.os-value {
	color: #000000 !important;
}



/* removes gray box graphics from right sidebars */
.os-sidebarRight,
div.os-sidebar div.os-component div.os-panel div.os-body,
div.os-sidebar div.os-component div,
div.os-sidebar div.os-component {
	background-image: none !important ;
}

/* sidebar boxez, top */
div.os-sidebar div.os-component div.os-panel div.os-head {
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack_top.png) !important ;
	background-color: #111214 !important ;
	background-repeat: repeat-x !important ;
	border-top: 1px solid #111214 !important ;
}

/* sidebar boxes, left and right */
div.os-sidebar div.os-component div.os-panel {
	background-color: #111214 !important ;
}

/* sidebar boxes, bottom */
div.os-sidebar div.os-component {
	background-image: url(http://www.angehowe.com/delphi/skins/blueblack_bottom.png) !important ;
	background-color: #111214 !important ;
	background-repeat: repeat-x !important ;
	border-top: 0 solid !important;
	border-right: 1px solid #2a2f38 !important;
	border-left: 1px solid #2a2f38 !important;
	border-bottom: 1px solid #111214 !important;
}

/* Bottom Promo header text color */
div.os-myforums div.os-myforumsfoot h2 {
	color: #1d6fca;
}

/* aligns message count column to the right */
td.os-messages {
	text-align: right !important;
	white-space: nowrap;
}

th.os-messages {
	text-align: right !important;
}

/* Shifts display boxes to center */
.os-visible {
	text-align: center !important;
}

/* Display and Remove/Add section below MyForums lists */
table.os-contenttable tr.os-foot td,
table.os-contenttable tr.os-ButtonBar td {
	background-color: #000 !important ;
}

/* bottom promos */
.os-body li div {
	background-color: transparent !important ;
}

/* Bottom promo text color */
.os-info {
	color: #dedede !important ;
}


/* Recolors some text that looks like a link */
.os-body, .os-info {
	color: #dedede !important;
}

/* removes the hover bg color in profiles */
table.os-InboxTable tr.os-ActionableItem td,
div.os-ActionableItem





/* Widget pop up editor - body */
div.os-PopinWindow div.os-Popin div.os-PopinContent div.os-JabberForm {
	background-color: #171717 !important;
	color: white !important;
}

.os-field { 
	color: white ; 
}

div.os-JabberForm div.os-field input, 
div.os-JabberForm  div.os-field textarea {
	color: #000 !important;
}

/* my files - files header */
.os-FileTable tr.os-header td,
.os-ButtonBar,
td.os-control {
	background-color: #171717 !important;
	color: #ffffff !important;
}

/* text in some profile menus */
.os-DropDown {
color: #000 !important;
}
table.os-myforums input {
	filter:alpha(opacity=20);
	opacity:0.2;
	-moz-opacity:0.2;
}

table.os-myforums input:hover {
	filter:alpha(opacity=100);
	opacity:1;
	-moz-opacity:1;
}

td.os-forum {
	white-space: nowrap !important;
	max-width: 200px !important;
}

tr.ptdHead th.os-visible {
	color: #000000 !important;
}

.os-myforumsfoot {
background-color: #000 !important;
}

.os-page-footer {
background-color: #171717 !important;
}

}
