css:
@-moz-document url-prefix(https://www.deepl.com/translator) {
        * {
            background-color: #0e1017 !important;
            color: #C2ECFF !important;
            transition: unset !important;
        }
        .lmt__target_textarea, .lmt__inner_textarea_container, .lmt__translations_as_text__header, .lmt__textarea_dummydiv {
            background: transparent !important;
        }


        /* width */
        ::-webkit-scrollbar {
          width: 0.5vw;
        }

        /* Track */
        ::-webkit-scrollbar-track {
          background: #0e1017;
        }

        /* Handle */
        ::-webkit-scrollbar-thumb {
          border-radius: 6px;
          background: #43ADFC;
        }

        /* Handle on hover */
        ::-webkit-scrollbar-thumb:hover {
          background: #286999;
        }

        path {
            fill: #0e1017;
        }


        header {
            display: none !important;
        }
        footer {
            display: none !important;
        }

        .dl_ad_pro_container {
            display: none !important;
        }
        #lmt_quotes_article {
            display: none !important;
        }
        .eSEOtericText {
            display: none !important;
        }
        .lmt__translations_as_text {
            display: flex !important;
        }
}
