css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("b.hatena.ne.jp") {

@charset "utf-8";

	/*
	===== User stylesheet information =========================
	
	Name:        Hatena Bookmark - http://b.hatena.ne.jp/
	Version:     1.00
	Author:      Sakatori - http://az-store.nrym.org/
	Description: http://az-store.nrym.org/download/user-stylesheet/hatena/bookmark
	
	更新履歴
	1.00:        2009.11.01
	1.01:        2011-04-06
	
	===========================================================
	*/

	/* 背景画像を非表示にする */
body
	{
	background-image: none !important;
	}

	/* ヘッダー固定解除 */
#header
	{
	position: absolute !important;
	}

	/* サイドバーのズレを修正 */
#sidebar
	{
	margin-top: -13px !important;
	}

	/* 個人のブックマークページの「お気に入り」ユーザーのコメントの色を薄くする */
.others
	{
	color: #999999 !important;
	}

	/* ブックマークの削除・修正ボタンを常に表示させる */
.inplace-edit-icon,
.inplace-delete-icon
	{
	display: inline !important;
	}

	/* タグスポンサーを普通に表示する */
.sponsored-tag
	{
	font-weight: normal !important;
	color: #6666cc !important;
	}

	/* 非表示 */
#guest-message,
.message,
#ad,
[class*=ads],
#asin,
#info-header,
.ad-line,
.sidebar-asin-sim,
.hatena-star-star-container,
.plus_icon,
#user-config-pr,
#tag-sponsor-main-container,
.curvebox-gray-header,
#sidebar,
#entrylist_ad,
.twitter,
#pin-header,
.tweets,
#tweet-counter,
#click-counter,
#bookmark-status,
#scored-bookmarks
	{
	display: none !important;
	}

.curvebox-body,
.has-sidebar,
.hotentry,
.entry-body
	{
	width: auto !important;
	float: none !important;
	margin-right: 0 !important;
	margin-left: 0 !important;
	}

.has-sidebar
	{
	padding-left: 0 !important;
	padding-right: 0 !important;
	}

#container,
#main
	{
	min-width: 0 !important;
	max-width: 100% !important;
	width: auto !important;
	margin-left: 15px !important;
	margin-right: 15px !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
	float: none !important;
	}

.curvebox-body
	{
	padding-right: 20px !important;
	}

.entry-body blockquote
	{
	max-width: 100% !important;
	width: auto !important;
	margin-left: 0 !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
	}

	/* 記事ごとに色を分ける（未使用） */
	/* 
.hotentry>li:nth-child(even)
	{
	background:#eeeeee !important;
	}
	 */

}
