css:
@-moz-document url-prefix("https://panzoid.com/") {
    /* An edited version of CJ's Autumn Theme (I didn't know where to start with this even tho i know css cause i'm dumb asf */
    @keyframes rainbow_clip {
        from {
            -webkit-filter: hue-rotate(-90deg);
        }
        to {
            -webkit-filter: hue-rotate(270deg);
        }
    }
    @keyframes rainbow {
        from {
            -webkit-filter: hue-rotate(-90deg);
        }
        to {
            -webkit-filter: hue-rotate(270deg);
        }
    }
    .clip .selected {
        background-color: #39ffc4;
    }
    .clip > div {
        color: #000000!important;
        filter: drop-shadow(0 0 3px #ffffff75);
    }
    .clip {
        height: 100%;
        background-color: #39ffc4;
        border: 1px solid #9ba4ff;
        box-sizing: border-box;
        position: absolute;
        cursor: default;
        overflow: hidden;
        border-left-color: #9ba4ff;
        border-top: 0;
        border-bottom: 0;
    }
    .elevator a.selected {
        background-image: linear-gradient(to left, #a81934 15%, #FF2C55);
        animation: rainbow 10s infinite linear;
    }
    .pzinput {
        padding: 2px;
        border: 1px solid transparent;
        background-image: linear-gradient(to left, #ff6180 15%, #ff5274);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        background: -webkit-gradient(linear, left, right, color-stop(0.15, #ff6180), color-stop(1, #FF2C55));
        background: -moz-linear-gradient(to left, #ff6180 15%, #FF2C55);
        background: -ms-linear-gradient(to left, #ff6180 15%, #FF2C55);
        background: -o-linear-gradient(to left, #ff6180 15%, #FF2C55);
        background: -webkit-linear-gradient(to left, #ff6180 15%, #FF2C55);
        animation: rainbow 10s infinite linear;
        cursor: move;
    }
    .pz-inputbox {
        border: 1px solid #1b1b1b;
        background-color: #202020;
        font-family: inherit;
        color: #ff5274;
        animation: rainbow 10s infinite linear;
        border-radius: 0;
        box-sizing: border-box;
    }
    .parent.selected {
        background-image: linear-gradient(to left, #a81934 15%, #FF2C55);
        animation: rainbow 10s infinite linear;
        color: #f8f8f8;
    }
    element.style {
        position: absolute;
        bottom: 0;
        height: 2px;
        width: 720px;
        left: 0;
        background-color: #fb9e88;
        opacity: 0.8;
    }
    body {
        margin: 0;
        top: 0;
        position: absolute;
        left: 0;
        right: 0;
        bottom: 0;
        background-color: #303030;
        font-family:'Source Sans Pro', sans-serif;
        outline-color: #ffffff;
    }
    ul.tree li.selected {
        background-image: linear-gradient(to left, #a81934 15%, #FF2C55);
        animation: rainbow 10s infinite linear;
    }
    body {
        background-color: #303030;
        outline-color: #303030;
    }
    div.header {
        width: 100%;
        background-color: #303030;
        border-bottom: 3px solid #ffffff25;
        height: 5px;
    }
    .el-selected {
        background-color: #ff5274!important;
    }
    #previewpane {
        background-color: #303030;
    }
    #previewtop {
        background-color: #303030;
        border-bottom: 3px solid #303030;
    }
    #previewbottom {
        background-color: #303030;
        border-top: 3px solid #303030;
    }
    element.style {
        border-right: 3px solid rgb(28, 190, 193);
    }
    .clip {
        height: 100%;
        background-image: linear-gradient(to left, #a81934 15%, #FF2C55);
        filter: saturate(80%);
        border: 1px solid #232323;
        box-sizing: border-box;
        position: absolute;
        cursor: default;
        overflow: hidden;
        border-left-color: #838383;
        border-top: 0;
        border-bottom: 0;
    }
    .clip.selected {
        filter: saturate(10%);
        animation: rainbow_clip 10s infinite linear;
    }
    a {
        color: #ffffff;
        text-decoration: none;
        transition: color .3s ease;
    }
    element.style {
        font-size: 20px;
        color: #ffffff;
    }
    a.homeheading {
        font-size: 18px;
        margin: 15px 0;
        color: #ffffff;
        text-decoration: none;
        display: inline-block;
        font-weight: 700;
    }
    a.creationTitle {
        color: #fff;
        font-size: 20px;
        margin-bottom: 3px;
        display: block;
    }
    a.creationUser {
        font-size: 16px;
        color: #797979;
    }
    .discussions {
        width: 100%;
        border-collapse: collapse;
        color: #ffa699;
    }
    element.style {
        font-size: 26px;
        color: #fdfdfd;
        text-shadow: 0px 0px 19px black;
    }
    div.post {
        display: flex;
        flex-direction: row;
        padding: 10px 0;
        max-width: 800px;
        border-bottom: 1px solid #ff5274;
        font-size: 17px;
    }
    div.post_message {
        margin-bottom: 30px;
        font-family:'Source Sans Pro', sans-serif;
        word-wrap: break-word;
        color: #efefef;
    }
    .button {
        display: inline-block;
        padding: 0 7px;
        background-color: #ff4122;
        color: #e9ecef!important;
        text-decoration: none;
        transition: background-color .4s ease;
        line-height: 30px;
    }
    div.post_info {
        margin-bottom: 15px;
        display: flex;
        flex-direction: row;
        justify-content: space-between;
        color: #ff5274;
    }
    .more {
        margin: 10px auto auto;
        display: block;
        text-decoration: none;
        background-color: #303030;
        color: #ffffff!important;
        border: 2px solid #ff5274;
        padding: 8px 0;
        width: 200px;
        text-align: center;
        transition: border .4s ease;
    }
    element.style {
        font-size: 26px;
        color: white;
    }
    a:hover {
        color: #ff5274;
    }
    td {
        padding: 5px;
        border-bottom: 1px solid #ff5274;
    }
    user agent stylesheet td, th {
        display: table-cell;
        vertical-align: inherit;
    }
    th {
        text-align: left;
        border-bottom: 2px solid #ff5274;
        font-weight: 400;
        color: #ff5274;
        padding: 5px;
        line-height: 30px;
        font-size: 18px;
    }
    .sections a.selected {
        border-bottom: 3px solid #ff5274;
        font-weight: 700;
    }
    .sections a:hover {
        border-bottom: 3px solid #ffffff;
    }
    a:hover {
        color: #ff5274;
    }
    .sections a {
        display: inline-block;
        padding: 0 6px;
        color: #ffffff;
        text-decoration: none;
        transition: background-color .25s ease;
        margin-right: 5px;
        text-transform: capitalize;
    }
    .sections svg {
        width: 16px;
        fill: #ff5274;
        height: 16px;
        vertical-align: middle;
    }
    button.cat {
        background-color: #303030;
        border: 1px solid #ff5e39;
        padding: 0 10px;
        display: inline-block;
        color: #ffffff;
        text-decoration: none;
        margin-right: 5px;
    }
    .num {
        text-align: center;
        color: #ffffff;
    }
}
