@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.anandtech.com") {

/* ----- Theme Colors - Dark Theme #2 ----- */

tbody[id^='threadbits'] tr:nth-child(odd) td { background-color: rgb(50,50,50)!important; }
tbody[id^='threadbits'] tr:nth-child(even) td { background-color: rgb(35,35,35)!important; }

/* -- Text -- */
/* main text color */
.page, .thead, .tcat, .alt1, .alt2, .alt1Active, .alt2Active, .vbmenu_control, .time, .panel, legend,.vbmenu_option { color: rgb(220,220,220)!important; }

/* links */
a:link { color: rgb(90,140,190)!important; }
a:visited { color: rgb(153,102,204)!important; }
a:hover, a:visited:hover { color: rgb(153,102,204)!important; }
.vbmenu_control a { color: rgb(220,220,220)!important; }
.vbmenu_control a:hover { color: rgb(153,102,204)!important; }
.thead a:link { color: rgb(220,220,220)!important; }
.alt1 a:link { font-weight: normal!important; }
.alt2 a:link { font-weight: normal!important; }
.alt1 a:visited { font-weight: normal!important; }
.alt2 a:visited { font-weight: normal!important; }
tbody[id^='collapseobj_forumbit'] a, a[id^='thread_title'] { text-decoration:none!important; font-weight:bold!important; }

/* nav menu */
tbody[id='collapseobj_leftsidebar_1'] a:link { color: rgb(90,140,190)!important; }
tbody[id='collapseobj_leftsidebar_1'] a:visited { color: rgb(153,102,204)!important; }
tbody[id='collapseobj_leftsidebar_1'] a:hover { color: rgb(153,102,204)!important; }
tbody[id='collapseobj_leftsidebar_1'] .alt2, tbody[id='collapseobj_leftsidebar_1'] .alt2Active { color:rgb(155,155,155)!important; }

/* !-- End Text -- */


/* -- Borders -- */
.tborder { border:none!important; background: rgb(20,20,20)!important; }
td { border-color:rgb(20,20,20)!important; }

/* forum fixed width */
div[class='page'] { max-width:1100px!important; }
#posts div[align='left'] { padding-left:0px!important; padding-right:0px!important; }
#posts + div[align='center'] div[class='page'] div[align='left'] { padding-left:0px!important; padding-right:0px!important; }

/* border re-alignment */
div[id^='edit'] { padding-bottom:0px!important; }
.thead, .alt1, .alt2, .tborder { border:none!important; }

/* move jump menu to top right */
div[class='smallfont'] > form[action='forumdisplay.php'] { position:absolute!important; top:0!important; right:0!important; }

/* !-- End Borders -- */


/* -- Backgrounds -- */
body,.page { background-color: rgb(0,0,0)!important; }

div[style^='border-bottom'] { border-color: rgb(0,0,0)!important; }
div[style^='background'] { background-color: rgb(0,0,0)!important; }

.tcat, .vbmenu_control { background:rgb(20,20,20)!important; }
.panel, .vBulletin_editor { background: rgb(20,20,20)!important; }
.thead, .tfoot { background-color: rgb(35,35,35)!important; }
.panelsurround, .vbmenu_option { background: rgb(35,35,35)!important; }
.alt1, .alt1Active { background-color: rgb(50,50,50)!important; }
.alt2, .alt2Active { background-color: rgb(50,50,50)!important; }

/* avatar panel */
/*table[id^='post'] .alt2, table[id^='post'] .alt2Active { background-color: rgb(228,228,228)!important; }*/

/* opera bug, reply box controls */
#vB_Editor_QR_controls div { background: rgb(225,225,226)!important; }

/* !-- End backgrounds -- */


/* !----- End Dark Theme #2 ------ */


/* -- Remove Images -- */
/* Replace Images */
img[src$="images/buttons/newthread.gif"] {
width:89px !important; padding-top:25px !important; height:0 !important; overflow:hidden !important;
background-image:url(data:image/png;base64,) !important;
background-repeat:no-repeat !important;
}

img[src$="images/buttons/reply.gif"] {
width:80px !important; padding-top:25px !important; height:0 !important; overflow:hidden !important;
background-image:url(data:image/png;base64,) !important;
background-repeat:no-repeat !important;
}

